// When telgram sees a message it will make sure its a reply to its message and from the user. 
// If thats the case then it will return {entry: string, date: string}

const botUsername = 'BOT_USERNAME'
const user = 'YOUR_USERNAME'

const res = item.message

const isReplyToBot = res.reply_to_message.from.username === botUsername
const isFromUser = res.from.username === user

// This assumes your message is formatted as follows: "SOME CUSTOM MESSAGE: YYYY-MM-DD"
const date = res.reply_to_message.text.split(':')[1].replace(/\s/g, '');

const journalEntry = res.text

if (isReplyToBot && isFromUser) {
  return {entry: journalEntry, date}
}

return undefined;
