const google_sheets_data = $('On new or updated row').all();

/**
 * Takes a list of objects and returns an ascii table with
 * padding and headers.
 */
function ascii_table(data, columns_to_display) {
  let table = ""

  // Get the headers
  let headers = []
  for (let i = 0; i < columns_to_display.length; i++) {
    headers.push(columns_to_display[i])
  }

  // Get the longest string in each column
  let longest_strings = []
  for (let i = 0; i < headers.length; i++) {
    let longest_string = headers[i].length
    for (let j = 0; j < data.length; j++) {
      let string_length = data[j].json[headers[i]].length
      if (string_length > longest_string) {
        longest_string = string_length
      }
    }
    longest_strings.push(longest_string)
  }

  // Add the headers to the table
  for (let i = 0; i < headers.length; i++) {
    table += headers[i].toString().padEnd(longest_strings[i] + 2, " ")
  }

  // Add the data to the table
  for (let i = 0; i < data.length; i++) {
    table += "\n"
    for (let j = 0; j < headers.length; j++) {
      table += data[i].json[headers[j]].toString().padEnd(longest_strings[j] + 2, " ")
    }
  }

  return table
}

output = {
  ascii_table: ascii_table(google_sheets_data, columns_to_display),
}

