parameters.query:
={
  inventoryItem(id: "gid://shopify/InventoryItem/{{ $json.inventory_tem }}") {
    id
    variant {
      id
      title
      inventoryQuantity  # This line adds the inventory quantity field
      product {
        id
        title
        images(first: 1) {
          edges {
            node {
              originalSrc
            }
          }
        }
      }
    }
  }
}

