parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "social_media": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "platform": {
            "type": "string",
            "description": "The name of the social media platform (e.g., LinkedIn, Instagram)"
          },
          "urls": {
            "type": "array",
            "items": {
              "type": "string",
              "format": "uri",
              "description": "A URL for the social media platform"
            }
          }
        },
        "required": ["platform", "urls"],
        "additionalProperties": false
      }
    }
  },
  "required": ["platforms"],
  "additionalProperties": false
}
