function replacePlaceholders(template, values) {
    // Regular expression to find placeholders in the format $some.place.holder$
    const placeholderPattern = /\$(.*?)\$/g;

    // Replace function to look up the value from the object
    return template.replace(placeholderPattern, (match, p1) => {
        // Split the placeholder into parts by dot notation
        const keys = p1.split('.');
        let value = values;

        // Traverse the object based on the dot notation
        for (const key of keys) {
            if (value && key in value) {
                value = value[key];
            } else {
                // If the key is not found, return the original placeholder
                return match;
            }
        }
        // Return the value found in the object
        return value;
    });
}

const templateTitle = $('Confluence: Get template content').item.json.name;
const templateBody = $('Confluence: Get template content').item.json.body.storage.value;
const values = $('Webhook').item.json;

const pageTitle = replacePlaceholders(templateTitle, values); 
const pageBody = replacePlaceholders(templateBody, values);

return { "page_title": pageTitle, "page_body" :  pageBody};
