const items = $input.all();
const result = [];

items.forEach((item) => {
  const trackUris = item.json.trackUris;
  if (trackUris.length > 100) {
    for (let i = 0; i < trackUris.length; i += 100) {
      const newItem = { ...item.json, trackUris: trackUris.slice(i, i + 100) };
      result.push(newItem);
    }
  } else {
    result.push(item.json);
  }
});

return result;

const items = $input.all();
const chunkSize = 200;
const result = [];

for (let i = 0; i < items.length; i += chunkSize) {
  const chunk = items.slice(i, i + chunkSize).map((item) => item.json);
  result.push({json:{chunk}}); // Wrap each chunk in an object with a json property
}

return result;

