parameters.jsonBody:
=  {
    "trigger_id": "{{ $('Parse Webhook').item.json['response']['trigger_id'] }}",
    "external_id": "Image Uploader",
    "view": {
	"title": {
		"type": "plain_text",
		"text": "File Upload - Select",
		"emoji": true
	},
	"type": "modal",
	"external_id": "file_upload_selector",
	"close": {
		"type": "plain_text",
		"text": "Cancel",
		"emoji": true
	},
	"blocks": [
		{
			"type": "section",
			"block_id": "greeting_section",
			"text": {
				"type": "plain_text",
				"text": ":wave: Hey {{ $('Route Message').item.json.response.user.username }}!\n\nNeed to upload an image to a public repository? If so, you've come to the right place. Use the form below to upload your images to our public S3 CDN. You will get a message with the link to the file after submission. This tool only accepts .jpg, .png, and .pdf uploads.",
				"emoji": true
			}
		},
		{
			"type": "divider",
			"block_id": "divider_1"
		},
		{
			"type": "actions",
			"block_id": "folder_type_selection",
			"elements": [
				{
					"type": "radio_buttons",
					"options": [
						{
							"text": {
								"type": "plain_text",
								"text": "Create New Folder",
								"emoji": true
							},
							"value": "createfolder"
						},
						{
							"text": {
								"type": "plain_text",
								"text": "Use Existing Folder",
								"emoji": true
							},
							"value": "selectfolder"
						}
					],
					"action_id": "folder-type"
				}
			]
		}
	]
}
}

=  {
    "trigger_id": "{{ $('Parse Webhook').item.json['response']['trigger_id'] }}",
    "view": {
	"title": {
		"type": "plain_text",
		"text": "File Upload - New Folder",
		"emoji": true
	},
	"submit": {
		"type": "plain_text",
		"text": "Upload",
		"emoji": true
	},
	"type": "modal",
	"external_id": "file_upload_new_folder",
	"close": {
		"type": "plain_text",
		"text": "Cancel",
		"emoji": true
	},
	"blocks": [
		{
			"type": "section",
			"block_id": "greeting_section",
			"text": {
				"type": "plain_text",
				"text": ":wave: Hey there!\n\nNeed to upload an image to a public repository? If so, you've come to the right place. Use the form below to upload your images to our public S3 CDN. You will get a message with the link to the file after submission. This tool only accepts .jpg, .png, and .pdf uploads.",
				"emoji": true
			}
		},
		{
			"type": "divider",
			"block_id": "divider_1"
		},
		{
			"type": "input",
			"block_id": "folder_name_block",
			"element": {
				"type": "plain_text_input",
				"action_id": "folder_name_input_action",
				"placeholder": {
					"type": "plain_text",
					"text": "example_folder_name"
				}
			},
			"label": {
				"type": "plain_text",
				"text": "Folder Name",
				"emoji": true
			}
		},
		{
			"type": "context",
			"block_id": "folder_creation_context",
			"elements": [
				{
					"type": "plain_text",
					"text": "This will create a new folder in the CDN.",
					"emoji": true
				}
			]
		},
		{
			"type": "input",
			"block_id": "input_block_file",
			"label": {
				"type": "plain_text",
				"text": "Image File Binary"
			},
			"element": {
				"type": "file_input",
				"action_id": "file_input_action",
				"filetypes": [
					"jpg",
					"png",
					"pdf"
				],
				"max_files": 10
			}
		},
		{
			"type": "context",
			"elements": [
				{
					"type": "plain_text",
					"text": "You can upload up to 10 files at a time.",
					"emoji": true
				}
			]
		}
	]
}
}

