let knownAttributesOutput = $('All Attributes').all();
let knownAttributes = new Map();
knownAttributesOutput.forEach((nodeOutput)=>{
knownAttributes.set(nodeOutput.json.attribute.toString().trim(), nodeOutput.json.id);
});


for (const item of $input.all()) {
  item.json.attributes.forEach((attribute, index)=>{
    item.json.attributes[index] = knownAttributes.get(attribute.toString().trim());
  });
}

return $input.all();

let knownSimilarsOutput = $('All Similar').all();
let knownSimilars = new Map();
knownSimilarsOutput.forEach((nodeOutput)=>{
  knownSimilars.set(nodeOutput.json.similar.toString().trim(), nodeOutput.json.id);
});

for (const item of $input.all()) {
  item.json.similar.forEach((similar, index)=>{
    item.json.similar[index] = knownSimilars.get(similar.toString().trim());
  });
}

return $input.all();
