// Get the HTML content
const html = items[0].json.data;

// Search for the subtitles block in the HTML
const match = html.match(/\"captions\":\\{.*?\\}/);

if (!match) {
    throw new Error("No subtitles information found in the HTML.");
}

// Extract the subtitles block
const captionsBlock = match[0];

// Search for all base URLs of subtitles
const baseUrlMatches = [...captionsBlock.matchAll(/\"baseUrl\":\"(.*?)\"/g)];

if (!baseUrlMatches.length) {
    throw new Error("No subtitle URLs found.");
}

// Decode and clean all base URLs
const baseUrls = baseUrlMatches.map(match => 
    decodeURIComponent(match[1]).replace(/\\\u0026/g, "&")
);

// Return all URLs as the node's output
return baseUrls.map(url => ({ json: { baseUrl: url } }));
