// Retrieve all input data from the previous node
const items = $input.all();

// Initialize an array to store the URIs
let uris = [];
let answer = "";
let reasoning = "";

// Process each input item
items.forEach(item => {
  // Navigate to the citationSources array within citationMetadata
  const citationSources = item.json.candidates?.[0]?.citationMetadata?.citationSources;

  // Check if citationSources exists and is an array
  if (Array.isArray(citationSources)) {
    // Extract the URI from each source and add it to the uris array
    citationSources.forEach(source => {
      if (source.uri) {
        uris.push(source.uri);
      }
    });
  }

  // Extract reasoning and answer
  reasoning = item.json.candidates?.[0]?.content?.parts?.[0]?.text || "";
  answer = item.json.candidates?.[0]?.content?.parts?.[1]?.text || "";
});

// Wrap the URIs, reasoning, and answer inside a single response object
return uris.length > 0 || reasoning || answer 
  ? [{ json: { response: { citation: uris, reasoning, answer } } }] 
  : [];
