parameters.responseBody:
={
    "response_type": "in_channel",
    "blocks": [
        {
            "type": "header",
            "text": {
                "type": "plain_text",
                "text": "ServiceNow Incident Notification",
                "emoji": true
            }
        },
        {
            "type": "section",
            "fields": [
                {
                    "type": "mrkdwn",
                    "text": "*Incident ID:*\n{{ $('Search For Incident in ServiceNow').item.json.number }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Description:*\n{{ $('Search For Incident in ServiceNow').item.json.short_description }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Severity:*\n{{ $('Search For Incident in ServiceNow').item.json.severity }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Caller:*\n{{ $('Search For Incident in ServiceNow').item.json.caller_id.display_value }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Priority:*\n{{ $('Search For Incident in ServiceNow').item.json.priority }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*State:*\n{{ $('Search For Incident in ServiceNow').item.json.incident_state }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Category:*\n{{ $('Search For Incident in ServiceNow').item.json.category }}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Date Opened:*\n{{ $('Search For Incident in ServiceNow').item.json.opened_at }}"
                }
            ]
        },
        {
            "type": "actions",
            "elements": [
                {
                    "type": "button",
                    "text": {
                        "type": "plain_text",
                        "text": "View Incident",
                        "emoji": true
                    },
                    "url": "https://dev206761.service-now.com/nav_to.do?uri=incident.do?sys_id={{ $('Search For Incident in ServiceNow').item.json.sys_id }}",
                    "action_id": "view_incident"
                }
            ]
        }
    ]
}

={
	"blocks": [
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": ":warning: No incident was found with that ID. Please double check and try again. :warning:"
			}
		}
	]
}

={
	"blocks": [
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": ":rotating_light: Issue connecting to ServiceNow. Please investigate in n8n. :rotating_light:"
			}
		}
	]
}
