parameters.jsonBody:
=  {
    "trigger_id": "{{ $('Parse Webhook').item.json['response']['trigger_id'] }}",
    "external_id": "Search SNOW Incidents",
    "view": {
	"title": {
		"type": "plain_text",
		"text": "Search SNOW Incidents",
		"emoji": true
	},
	"submit": {
		"type": "plain_text",
		"text": "Search",
		"emoji": true
	},
	"type": "modal",
	"external_id": "search_snow_incidents",
	"close": {
		"type": "plain_text",
		"text": "Cancel",
		"emoji": true
	},
	"blocks": [
		{
			"type": "section",
			"block_id": "greeting_section",
			"text": {
				"type": "plain_text",
				"text": ":wave: Hey there!\n\nUse this form to search ServiceNow for incidents based on their priority and state. Both of these properties are required to search incidents properly.",
				"emoji": true
			}
		},
		{
			"type": "divider",
			"block_id": "divider_1"
		},
		{
			"type": "input",
			"block_id": "priority_selector",
			"element": {
				"type": "external_select",
				"placeholder": {
					"type": "plain_text",
					"text": "Priority of Incidents to Search",
					"emoji": true
				},
				"action_id": "priority_select",
				"min_query_length": 0
			},
			"label": {
				"type": "plain_text",
				"text": "Priority Selector",
				"emoji": true
			}
		},
		{
			"type": "input",
			"block_id": "state_selector",
			"element": {
				"type": "external_select",
				"placeholder": {
					"type": "plain_text",
					"text": "State of Incidents to Search",
					"emoji": true
				},
				"action_id": "state_select",
				"min_query_length": 0
			},
			"label": {
				"type": "plain_text",
				"text": "State Selector",
				"emoji": true
			}
		},
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": "Please select a channel where the results will be posted."
			}
		},
		{
			"type": "actions",
			"elements": [
				{
					"type": "channels_select",
					"placeholder": {
						"type": "plain_text",
						"text": "Select a channel",
						"emoji": true
					},
					"action_id": "actionId-1"
				}
			]
		}
	]
}
}

assignments.value:
={
	"blocks": [
		{
			"type": "section",
			"block_id": "greeting_section",
			"text": {
				"type": "mrkdwn",
				"text": ":wave: Hey there!\n\nHere are the incident summaries you requested with a state of {{ $('Parse Webhook').item.json.response.view.state.values.state_selector.state_select.selected_option.text.text }} and priority of {{ $('Parse Webhook').item.json.response.view.state.values.priority_selector.priority_select.selected_option.text.text }}.\nA total of {{ $('ServiceNow').all().length }} incident(s) were found. If more than 5 were found only the 5 most recent will be listed. You can <https://dev206761.service-now.com/now/nav/ui/classic/params/target/incident_list.do%3Fsysparm_query%3Dincident_state%253D{{ $('Parse Webhook').item.json.response.view.state.values.state_selector.state_select.selected_option.value }}%255Epriority%253D{{ $('Parse Webhook').item.json.response.view.state.values.priority_selector.priority_select.selected_option.value }}%26sysparm_first_row%3D1%26sysparm_view%3Dess|click here> to view all of the matching incidents in ServiceNow."
			}
		},
		{
			"type": "divider"
		},
		{{ $('Concatenate Incident Details').item.json.concatenated_incident_details }}
	]
}
