={{ 
  (() => {
    // Input format example: '2025-01-31 13:00'
    const rawDate = $json['Termin Auswahl:']; 

    // Ensure the raw date is provided and in the expected format
    if (!rawDate || typeof rawDate !== 'string') return ''; // Return empty string if invalid

    // Split the date and time into components
    const [datePart, timePart] = rawDate.split(' '); // Example: ['2025-01-31', '13:00']
    if (!datePart || !timePart) return ''; // Return empty string if date or time is missing

    // Validate the date format (YYYY-MM-DD)
    const [year, month, day] = datePart.split('-'); // Split year, month, day
    if (!year || !month || !day || year.length !== 4 || month.length !== 2 || day.length !== 2) return ''; // Validate format

    // Combine into ISO 8601 format (YYYY-MM-DDTHH:mm) with Germany's local timezone offset
    const isoDateTime = `${year}-${month}-${day}T${timePart}:00+01:00`;

    // Create a Date object in Germany's timezone
    const localDate = new Date(isoDateTime);

    // Convert the local time to a UTC UNIX timestamp in seconds
    return Math.floor(localDate.getTime() / 1000); 
  })()
}}

