// Input: An array with objects containing a 'value' array of events.
const businessHoursStart = "08:00:00Z";  // Business hours start time
const businessHoursEnd = "17:30:00Z";    // Business hours end time

const inputData = items[0].json.value;  // Assuming the input data is in the 'value' array of the first item

// Function to convert ISO datetime string to a Date object with specified time
function getDateWithTime(dateString, time) {
  const datePart = new Date(dateString).toISOString().split("T")[0];  // Extract the date part (YYYY-MM-DD)
  return new Date(`${datePart}T${time}`);
}

// Function to get day of the week from a date string
function getDayOfWeek(dateString) {
  const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  return daysOfWeek[new Date(dateString).getUTCDay()];
}

// Function to add days to a date
function addDays(date, days) {
  const result = new Date(date);
  result.setDate(result.getDate() + days);
  return result;
}

// Function to format date as YYYY-MM-DD
function formatDate(date) {
  return date.toISOString().split('T')[0];
}

// Determine the default timezone from input data
const defaultTimeZone = inputData.length > 0 && inputData[0].start && inputData[0].start.timeZone 
  ? inputData[0].start.timeZone 
  : "UTC";

// Find min and max dates in the input
let minDate = null;
let maxDate = null;

inputData.forEach(event => {
  if (event.start && event.start.dateTime) {
    const eventDate = new Date(event.start.dateTime);
    if (!minDate || eventDate < minDate) {
      minDate = eventDate;
    }
    if (!maxDate || eventDate > maxDate) {
      maxDate = eventDate;
    }
  }
});

// If we have valid dates, ensure they're at the start of the day
if (minDate && maxDate) {
  minDate = new Date(minDate.toISOString().split('T')[0]);
  maxDate = new Date(maxDate.toISOString().split('T')[0]);
}

// Organise events by date
const eventsByDate = {};
inputData.forEach(event => {
  if (event.start && event.start.dateTime) {
    const eventDate = new Date(event.start.dateTime).toISOString().split("T")[0];  // Extract the date
    if (!eventsByDate[eventDate]) {
      eventsByDate[eventDate] = [];
    }
    if (event.showAs === "busy") {
      eventsByDate[eventDate].push({
        start: new Date(event.start.dateTime),
        end: new Date(event.end.dateTime),
        timeZone: event.start.timeZone || defaultTimeZone
      });
    }
  }
});

// Find free slots within business hours for each date
const freeTimeSlots = [];

// Process all dates in the range
if (minDate && maxDate) {
  for (let currentDate = new Date(minDate); currentDate <= maxDate; currentDate = addDays(currentDate, 1)) {
    const dateStr = formatDate(currentDate);
    const busyEvents = eventsByDate[dateStr] || [];

    // Define business start and end times for the current date
    const businessStart = getDateWithTime(dateStr, businessHoursStart);
    const businessEnd = getDateWithTime(dateStr, businessHoursEnd);

    // If there are no busy events for this date, add the entire business day as free
    if (busyEvents.length === 0) {
      freeTimeSlots.push({
        date: dateStr,
        dayOfWeek: getDayOfWeek(dateStr),
        freeStart: businessStart.toISOString(),
        freeEnd: businessEnd.toISOString(),
        timeZone: defaultTimeZone
      });
      continue; // Skip to the next date
    }

    // Sort events by their start time
    busyEvents.sort((a, b) => a.start - b.start);

    // Check if there's free time before the first busy event
    if (busyEvents[0].start > businessStart) {
      freeTimeSlots.push({
        date: dateStr,
        dayOfWeek: getDayOfWeek(dateStr),
        freeStart: businessStart.toISOString(),
        freeEnd: busyEvents[0].start.toISOString(),
        timeZone: busyEvents[0].timeZone
      });
    }

    // Check for gaps between busy events
    for (let i = 0; i < busyEvents.length - 1; i++) {
      if (busyEvents[i].end < busyEvents[i+1].start) {
        freeTimeSlots.push({
          date: dateStr,
          dayOfWeek: getDayOfWeek(dateStr),
          freeStart: busyEvents[i].end.toISOString(),
          freeEnd: busyEvents[i+1].start.toISOString(),
          timeZone: busyEvents[i].timeZone
        });
      }
    }

    // Check if there's free time after the last busy event
    if (busyEvents[busyEvents.length - 1].end < businessEnd) {
      freeTimeSlots.push({
        date: dateStr,
        dayOfWeek: getDayOfWeek(dateStr),
        freeStart: busyEvents[busyEvents.length - 1].end.toISOString(),
        freeEnd: businessEnd.toISOString(),
        timeZone: busyEvents[busyEvents.length - 1].timeZone
      });
    }
  }
}

// Output the free time slots
return [{ json: { freeTimeSlots } }];

