const inputData = $input.all(); // Fetch all input data items

// Function to convert JSON data into a single HTML string
function convertToHTML(data) {
    let html = '';

    data.forEach((item) => {
        switch (item.json.type) {
            case 'paragraph':
                html += `<p>${item.json.content}</p>`;
                break;
            case 'heading':
                html += `<h2>${item.json.content}</h2>`;
                break;
            case 'list':
                html += '<ul>';
                item.json.items.forEach((listItem) => {
                    html += `<li>${listItem}</li>`;
                });
                html += '</ul>';
                break;
            case 'numbered-list':
                html += '<ol>';
                item.json.items.forEach((listItem) => {
                    html += `<li>${listItem}</li>`;
                });
                html += '</ol>';
                break;
            default:
                break;
        }
    });

    return html;
}

// Convert inputData to a single HTML string
const singleHTML = convertToHTML(inputData);

// Return as a single item
return [{ json: { html: singleHTML } }];

