parameters.text:
=Give chapters for this book idea:  {{ $json.Idea }}
-----
Resrearch on the topic:
{{ $json.Research }}

parameters.inputSchema:
{
  "name": "chapters",
  "schema": {
    "type": "object",
    "properties": {
      "chapters": {
        "type": "array",
        "description": "A collection of chapters.",
        "items": {
          "type": "object",
          "properties": {
            "title": {
              "type": "string",
              "description": "The name of the chapter."
            },
            "description": {
              "type": "string",
              "description": "A brief description of the chapter."
            }
          },
          "required": [
            "title",
            "description"
          ],
          "additionalProperties": false
        }
      }
    },
    "required": [
      "chapters"
    ],
    "additionalProperties": false
  },
  "strict": true
}

