function jsonToMarkdown(blocks) {
    let markdown = "";

    function parseRichText(richTextArray) {
        return richTextArray.map(text => {
            let content = text.text.content;
            if (text.annotations.bold) content = `**${content}**`;
            if (text.annotations.italic) content = `*${content}*`;
            if (text.annotations.strikethrough) content = `~~${content}~~`;
            if (text.annotations.underline) content = `_${content}_`;
            if (text.annotations.code) content = `\`${content}\``;
            if (text.text.link) content = `[${content}](${text.text.link.url})`;
            return content;
        }).join("");
    }

    blocks.forEach(block => {
        switch (block.json.type) {
            case "heading_1":
                markdown += `\n# ${parseRichText(block.json.heading_1.rich_text)}\n`;
                break;
            case "heading_2":
                markdown += `\n## ${parseRichText(block.json.heading_2.rich_text)}\n`;
                break;
            case "heading_3":
                markdown += `\n### ${parseRichText(block.json.heading_3.rich_text)}\n`;
                break;
            case "paragraph":
                markdown += `\n${parseRichText(block.json.paragraph.rich_text)}\n`;
                break;
            case "bulleted_list_item":
                markdown += `- ${parseRichText(block.json.bulleted_list_item.rich_text)}\n`;
                break;
            case "numbered_list_item":
                markdown += `1. ${parseRichText(block.json.numbered_list_item.rich_text)}\n`;
                break;
            case "to_do":
                let checked = block.json.to_do.checked ? "[x]" : "[ ]";
                markdown += `- ${checked} ${parseRichText(block.json.to_do.rich_text)}\n`;
                break;
            case "quote":
                markdown += `\n> ${parseRichText(block.json.quote.rich_text)}\n`;
                break;
            case "code":
                markdown += `\n\
\`${block.code.language}\`\n\
${parseRichText(block.json.code.rich_text)}\n\
\n`;
                break;
            case "unsupported":
                break;
        }
    });

    return markdown.trim();
}

return [ { "md": jsonToMarkdown($input.all()) }];

function markdownToNotionBlocks(markdown) {
    const lines = markdown.split('\n');
    const blocks = [];
    let currentList = null;

    function parseRichText(text) {
        const richText = [];
        const regex = /(\*\*|__)(.*?)\1|(_|\*)(.*?)\3|(`)(.*?)\5|(\[)(.*?)\]\((.*?)\)/g;
        let lastIndex = 0;

        text.replace(regex, (match, bold1, boldText, italic1, italicText, code1, codeText, link1, linkText, linkUrl, index) => {
            if (index > lastIndex) {
                richText.push({ text: { content: text.slice(lastIndex, index) } });
            }

            if (boldText) {
                richText.push({ text: { content: boldText }, annotations: { bold: true } });
            } else if (italicText) {
                richText.push({ text: { content: italicText }, annotations: { italic: true } });
            } else if (codeText) {
                richText.push({ text: { content: codeText }, annotations: { code: true } });
            } else if (linkText) {
                richText.push({ text: { content: linkText, link: { url: linkUrl } } });
            }

            lastIndex = index + match.length;
        });

        if (lastIndex < text.length) {
            richText.push({ text: { content: text.slice(lastIndex) } });
        }

        return richText.length > 0 ? richText : [{ text: { content: text } }];
    }

    for (const line of lines) {
        if (line.startsWith('# ')) {
            blocks.push({ type: 'heading_1', heading_1: { rich_text: parseRichText(line.slice(2)) } });
        } else if (line.startsWith('## ')) {
            blocks.push({ type: 'heading_2', heading_2: { rich_text: parseRichText(line.slice(3)) } });
        } else if (line.startsWith('### ')) {
            blocks.push({ type: 'heading_3', heading_3: { rich_text: parseRichText(line.slice(4)) } });
        } else if (line.startsWith('- ')) {
            if (!currentList) {
                currentList = { type: 'bulleted_list_item', bulleted_list_item: { rich_text: parseRichText(line.slice(2)) } };
                blocks.push(currentList);
            } else {
                blocks.push({ type: 'bulleted_list_item', bulleted_list_item: { rich_text: parseRichText(line.slice(2)) } });
            }
        } else if (line.startsWith('> ')) {
            blocks.push({ type: 'quote', quote: { rich_text: parseRichText(line.slice(2)) } });
        } else if (line.startsWith('```')) {
            const codeLines = [];
            while (lines.length && !lines[0].startsWith('```')) {
                codeLines.push(lines.shift());
            }
            blocks.push({ type: 'code', code: { rich_text: [{ text: { content: codeLines.join('\n') } }] } });
        } else if (line.trim()) {
            blocks.push({ type: 'paragraph', paragraph: { rich_text: parseRichText(line) } });
        }
    }

    return blocks;
}


return { "blocks" : markdownToNotionBlocks($json.md)};
