// Get all input items
const items = $input.all();
const results = [];

// Import the YoutubeTranscript module from the youtube-transcript package
// npm i -g youtube-transcript
const { YoutubeTranscript } = require('youtube-transcript');

for (let i = 0; i < items.length; i++) {
  // Extract the VIDEO_ID from the input JSON
  const VIDEO_ID = items[i].json.VIDEO_ID;

  if (!VIDEO_ID) {
    throw new Error('The video ID parameter is empty.');
  }

  try {
    // Fetch the transcript for the provided video ID
    const transcript = await YoutubeTranscript.fetchTranscript(VIDEO_ID);

    // Append the fetched transcript data to the results
    results.push({
      json: {
        youtubeId: VIDEO_ID,
        transcript,
      },
    });
  } catch (error) {
    // In case of an error, add an error message to the output for this item
    results.push({
      json: {
        youtubeId: VIDEO_ID,
        error: error.message,
      },
    });
  }
}

// Return the results to be used by the next node in the workflow
return results;

// Define the base URL for the YouTube Data API
const BASE_URL = 'https://www.googleapis.com/youtube/v3/videos';

// Get the first input item
const item = $input.first();

// Extract the videoId and google_api_key from the input JSON
const VIDEO_ID = item.json.VIDEO_ID;
const GOOGLE_API_KEY = item.json.GOOGLE_API_KEY; // Dynamically retrieve API key

if (!VIDEO_ID) {
  throw new Error('The video ID parameter is empty.');
}

if (!GOOGLE_API_KEY) {
  throw new Error('The Google API Key is missing.');
}

// Construct the API URL with the video ID and dynamically retrieved API key
const youtubeUrl = `${BASE_URL}?part=snippet,contentDetails,status,statistics,player,topicDetails&id=${VIDEO_ID}&key=${GOOGLE_API_KEY}`;

// Return the constructed URL
return [
  {
    json: {
      youtubeUrl: youtubeUrl,
    },
  },
];
