// Define the base URL for the YouTube Data API
const BASE_URL = 'https://www.googleapis.com/youtube/v3/videos';

// Get the first input item
const item = $input.first();

// Extract the videoId and google_api_key from the input JSON
const VIDEO_ID = item.json.VIDEO_ID;
const GOOGLE_API_KEY = item.json.GOOGLE_API_KEY; // Dynamically retrieve API key

if (!VIDEO_ID) {
  throw new Error('The video ID parameter is empty.');
}

if (!GOOGLE_API_KEY) {
  throw new Error('The Google API Key is missing.');
}

// Construct the API URL with the video ID and dynamically retrieved API key
const youtubeUrl = `${BASE_URL}?part=snippet,contentDetails,status,statistics,player,topicDetails&id=${VIDEO_ID}&key=${GOOGLE_API_KEY}`;

// Return the constructed URL
return [
  {
    json: {
      youtubeUrl: youtubeUrl,
    },
  },
];

// Define the base URL for the YouTube Data API
const BASE_URL = 'https://www.googleapis.com/youtube/v3/commentThreads';

// Get the first input item
const item = $input.first();

// Extract the videoId and google_api_key from the input JSON
const VIDEO_ID = item.json.VIDEO_ID;
const GOOGLE_API_KEY = item.json.GOOGLE_API_KEY; // Dynamically retrieve API key
const MAX_RESULTS = 100;  //item.json.MAX_RESULTS;

const url = `${BASE_URL}?part=snippet&videoId=${encodeURIComponent(VIDEO_ID)}&key=${encodeURIComponent(GOOGLE_API_KEY)}&maxResults=${encodeURIComponent(MAX_RESULTS)}`;

// Now you can send this URL to the HTTP node for the GET request.
return { json: { url } };

// Define a helper function to build a query string from an object
function buildQueryString(params) {
	return Object.keys(params)
		.map(key => `${encodeURIComponent(key)}=${encodeURIComponent(params[key])}`)
		.join('&');
}

// Define the base URL for the YouTube Data API
const BASE_URL = "https://www.googleapis.com/youtube/v3/commentThreads";

// Get the first input item
const item = $input.first();

// Extract the videoId and google_api_key from the input JSON
const videoId = item.json.VIDEO_ID;
const apiKey = item.json.GOOGLE_API_KEY; // Dynamically retrieve API key

const comments = [];
let nextPageToken = undefined;

while (true) {
    // Construct URL parameters using an object literal
    const params = {
        part: "snippet",
        videoId: videoId,
        key: apiKey
    };

    if (nextPageToken) {
        params.pageToken = nextPageToken;
    }

    // Build the full URL without using URLSearchParams
    const queryString = buildQueryString(params);
    const url = `${BASE_URL}?${queryString}`;

    // Set up the options for the HTTP request helper
    const options = {
        method: "GET",
        uri: url,
        json: true
    };

    // Use n8n's built-in HTTP request helper instead of fetch
    const data = await this.helpers.request(options);

    // console.log(data.items)

    // Process each comment in the response
    data.items.forEach(item => {
        comments.push(item.snippet.topLevelComment.snippet.textOriginal);
    });

    // console.log(data.nextPageToken)

    // Exit loop if no further pages exist
    if (!data.nextPageToken) {
        break;
    }
    nextPageToken = data.nextPageToken;
}

// Return the collected comments as an item output for n8n
return [{ json: { comments } }];
