const delay = Math.floor(Math.random() * (6 * 60 * 60 * 1000)); // random delay 0-6 hour
return {
  json: {
    delay: delay,
    delay_minutes: Math.round(delay / 60000),  // to minutes
    delay_hours: (delay / 3600000).toFixed(2) // to hours
  }
};

