const newsItems = $input.all().map(item => item.json);
const emails = $("Gmail: Fetch Emails").all().map(item => item.json);
const tasks = $("TodoList: Fetch Tasks").all().map(item => item.json);

// Select top 5 items from each
const topNews = newsItems.slice(0, 5).map(item => ({
  title: item.title,
  link: item.link
}));

const latestEmails = emails.slice(0, 5).map(item => ({
  subject: item.Subject,
  snippet: item.snippet
}));

const topTasks = tasks.slice(0, 5).map(task => ({
  content: task.content,
  url: task.url,
  emoji: task.emoji || 'üî¥',
  due: task.due
}));

// Create the final JSON object with email subject and a formatted email body with inline CSS
const result = {
  meta: {
    generated_at: new Date().toISOString(),
    time_emoji: "üåû"
  },
  email: {
    subject: `üåû Daily Digest ‚Ä¢ üìã ${topTasks.length} Tasks ‚ö†Ô∏è ‚Ä¢ üì∞ ${topNews.length} News Updates`,
    body: `
      <div style="max-width:600px; margin:0 auto; font-family:'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; color:#333; background:#f4f7f9; padding:20px; border:1px solid #e1e8ed; border-radius:8px;">
         <div style="text-align:center; padding-bottom:20px;">
             <h1 style="margin:0; font-size:28px; color:#0073e6;">Daily Digest</h1>
             <p style="margin:10px 0 0; font-size:16px; color:#666;">Your automated daily summary</p>
         </div>
         <hr style="border:none; border-top:1px solid #ddd; margin:20px 0;">
         <div style="margin-bottom:20px;">
             <h2 style="font-size:20px; color:#0073e6; margin-bottom:10px;">Tasks (${topTasks.length})</h2>
             <ul style="list-style:none; padding:0;">
               ${topTasks.map(task => `
                 <li style="margin-bottom:10px; padding:10px; background:#fff; border:1px solid #e1e8ed; border-radius:4px;">
                   <span style="font-size:18px; margin-right:10px;">${task.emoji}</span> 
                   <span style="font-size:16px;">${task.content}</span> 
                   <span style="color:#999; font-size:14px; margin-left:5px;">(Due: ${task.due})</span>
                   <a href="${task.url}" style="text-decoration:none; color:#0073e6; float:right;">View Task</a>
                 </li>
               `).join('')}
             </ul>
         </div>
         <div style="margin-bottom:20px;">
             <h2 style="font-size:20px; color:#0073e6; margin-bottom:10px;">News (${topNews.length})</h2>
             <ul style="list-style:none; padding:0;">
               ${topNews.map(news => `
                 <li style="margin-bottom:10px; padding:10px; background:#fff; border:1px solid #e1e8ed; border-radius:4px;">
                   <a href="${news.link}" style="text-decoration:none; font-size:16px; color:#0073e6;">${news.title}</a>
                 </li>
               `).join('')}
             </ul>
         </div>
         <div style="margin-bottom:20px;">
             <h2 style="font-size:20px; color:#0073e6; margin-bottom:10px;">Emails (${latestEmails.length})</h2>
             <ul style="list-style:none; padding:0;">
               ${latestEmails.map(email => `
                 <li style="margin-bottom:10px; padding:10px; background:#fff; border:1px solid #e1e8ed; border-radius:4px;">
                   <strong style="font-size:16px; color:#0073e6;">${email.subject}</strong>
                   <p style="margin:5px 0 0; font-size:14px; color:#666;">${email.snippet}</p>
                 </li>
               `).join('')}
             </ul>
         </div>
         <div style="text-align:center; font-size:12px; color:#aaa; margin-top:20px;">
             <p>Digest generated at: ${new Date().toLocaleString()}</p>
         </div>
      </div>
    `
  },
  tasks: topTasks,
  news: topNews,
  emails: latestEmails
};

return [{ json: result }];
