parameters.jsCode:
try {
  if ($json.stdout === 'success') {
    return {
      json: {
        status: 'success',
        message: '',
        data: '',
      }
    };
  }

  const parsedData = JSON.parse($json.stdout);

  return {
    json: {
      status: parsedData.status === 'error' ? 'error' : 'success',
      message: parsedData.message || (parsedData.status === 'error' ? 'An error occurred' : ''),
      data: parsedData || '',
    }
  };

} catch (error) {
  return {
    json: {
      status: 'error',
      message: $json.stdout,
      data: '',
    }
  };
}

