(function(timestamp){
  const buffer = 5;
  let [hr,min,sec] = timestamp.map(x => Number(x));
  if (sec === undefined){ sec = min; min = hr; hr = 0 }
  return Math.max((hr * 60 * 60) + (min * 60) + sec - buffer, 0);
})($json.timestamp.split(':'))
}}

