(function(items) {
  const groupByVideoId = items.reduce((acc, item) => {
    if (!acc[item.videoId]) acc[item.videoId] = [];
    acc[item.videoId].push(item);
    return acc;
  }, {});

  const results = Object.keys(groupByVideoId).map(key => {
    const parts = groupByVideoId[key]
      .toSorted((a,b) => Number(a.video_ts) - Number(b.video_ts));
    const {title,url} = parts[0];
    return `<li video-id="${key}">
      <div class="resultSource">
        <a
          href="${url}"
          target="_blank"
          hx-on:click="loadVideo('${key}',0); event.preventDefault()"
        >
          <div class="resultSourceTitle">${title}</div>
          <div class="resultSourceUrl">${url}</div>
        </a>
      </div>
      ${parts.map(item => `
        <div class="resultContent">
          <a
            class="resultTimestamp"
            href="${item.url}&t=${item.video_ts}
            "target="_blank"
            hx-on:click="loadVideo('${item.videoId}',${item.video_ts}); event.preventDefault()"
          >
            [${item.timestamp}]
            <span>...${item.extract}...</span>
          </a>
        </div>`).join('\n')}
    </li>`
  }).join('\n')

  return `<div id="resultsCount">${Object.keys(groupByVideoId).length} Video Result${Object.keys(groupByVideoId).length === 1 ? '' : 's'}</div>${results}`;
})(
  $input.all().map(item => item.json).filter(item => item.videoId)
)
}}

