// Input data
const inputData = $input.all()[0].json.response;

// Define workday hours in CST
const WORKDAY_START = "09:00:00 CST";
const WORKDAY_END = "18:00:00 CST";
const SLOT_DURATION = 30 * 60 * 1000; // 30 minutes in milliseconds

// Helper to parse CST datetime strings
const parseCST = (datetime) => {
  const parsedDate = new Date(datetime.replace(" CST", "-06:00"));
  return isNaN(parsedDate) ? null : parsedDate;
};

// Function to generate 30-minute start times
const generateStartTimes = (start, end) => {
  const startTimes = [];
  let current = new Date(start);

  while (current < end) {
    startTimes.push(
      current.toLocaleTimeString('en-US', {
        timeZone: 'CST',
        hour: '2-digit',
        minute: '2-digit',
      })
    );
    current = new Date(current.getTime() + SLOT_DURATION);
  }

  return startTimes;
};

// Function to find wide open ranges
const findWideOpenRanges = (startTimes) => {
  if (startTimes.length < 3) return []; // Not enough slots for a wide open range

  const ranges = [];
  let rangeStart = null;
  let consecutiveCount = 0;

  for (let i = 0; i < startTimes.length - 1; i++) {
    const currentTime = parseCST(`2000-01-01 ${startTimes[i]} CST`);
    const nextTime = parseCST(`2000-01-01 ${startTimes[i + 1]} CST`);
    const diff = nextTime - currentTime;

    if (diff === SLOT_DURATION) {
      consecutiveCount += 1;
      if (rangeStart === null) rangeStart = startTimes[i];
    } else {
      if (consecutiveCount >= 2) {
        ranges.push(`${rangeStart} to ${startTimes[i]}`);
      }
      rangeStart = null;
      consecutiveCount = 0;
    }
  }

  // Handle the final range
  if (consecutiveCount >= 2) {
    ranges.push(`${rangeStart} to ${startTimes[startTimes.length - 1]}`);
  }

  return ranges;
};

// Group meetings by date, ignoring invalid dates
const meetingsByDate = inputData.reduce((acc, meeting) => {
  const start = parseCST(meeting.start);
  const end = parseCST(meeting.end);

  if (!start || !end) {
    return acc; // Ignore invalid dates
  }

  const dateKey = start.toISOString().split('T')[0];

  if (!acc[dateKey]) {
    acc[dateKey] = [];
  }

  acc[dateKey].push({ start, end });
  return acc;
}, {});

// Generate availability
const availability = Object.keys(meetingsByDate)
  .filter((date) => {
    // Exclude Saturdays (6) and Sundays (0)
    const dayOfWeek = new Date(date).getUTCDay();
    return dayOfWeek !== 0 && dayOfWeek !== 6;
  })
  .map((date) => {
    const workdayStart = parseCST(`${date} ${WORKDAY_START}`);
    const workdayEnd = parseCST(`${date} ${WORKDAY_END}`);

    const dayMeetings = meetingsByDate[date].sort((a, b) => a.start - b.start);

    let availableStartTimes = [];
    let lastEnd = workdayStart;

    for (const meeting of dayMeetings) {
      if (meeting.start > lastEnd) {
        availableStartTimes = availableStartTimes.concat(generateStartTimes(lastEnd, meeting.start));
      }
      lastEnd = meeting.end > lastEnd ? meeting.end : lastEnd;
    }

    if (lastEnd < workdayEnd) {
      availableStartTimes = availableStartTimes.concat(generateStartTimes(lastEnd, workdayEnd));
    }

    const wideOpenRanges = findWideOpenRanges(availableStartTimes);

    return {
      date: new Date(date).toLocaleDateString('en-US', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      }),
      availableStartTimes,
      wideOpenRanges,
    };
  });

// Format output as plaintext
const availableTimes = availability
  .map(({ date, availableStartTimes, wideOpenRanges }) => {
    const times = availableStartTimes.map((time) => `- ${time}`).join('\n');
    const ranges = wideOpenRanges.length
      ? `Wide Open Ranges:\n${wideOpenRanges.map((range) => `- ${range}`).join('\n')}`
      : "Wide Open Ranges: None";

    return `### ${date}\nAvailable Start Times:\n${times}\n\n${ranges}`;
  })
  .join('\n\n');

// Set the output
return {
  json: {
    availableTimes,
  },
};

const flattenSlots = (data) => {
  // If data is missing or empty, return an empty array of slots
  if (!data) {
    return { slots: [] };
  }

  // data is an object whose keys are dates
  // each date key has an array of slot objects
  // we just need to flatten them all into one array
  const flattened = Object.values(data).flat();  // merges all arrays from each date key

  // Return a new object with a single 'slots' array
  return { slots: flattened };
};

// Then assign the flattened slots back to $input.item.json.data
$input.item.json.data = flattenSlots($input.item.json.data);
return $input.item;

function formatTimeSlot(dateString) {
  // Format options for date/time with America/Chicago timezone
  const options = {
    timeZone: 'America/Chicago',
    weekday: 'long',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    hour12: true
  };

  // Create a formatter with timezone support
  const dateFormatter = new Intl.DateTimeFormat('en-US', options);

  // Format the date/time string
  return dateFormatter.format(new Date(dateString));
}

// Process each slot and add formatted time strings to the result
const slots = $input.item.json.data.slots;
const formattedSlots = slots.map(slot => formatTimeSlot(slot.start));

// Attach formatted results to the output
$input.item.json.data.slots = formattedSlots;

return $input.item;

function replaceValue(value) {
  if (error.message.include('no_available_users_found_error')) {
    return "This time slot is no longer available.";
  }
  return value;
}

$input.item.json.message = replaceValue($input.item.json.error.description);

return $input.item;

const escapeStringForJson = (str) => {
    return str
        .replace(/\\/g, '\\\\') // Escape backslashes
        .replace(/"/g, '\\"')    // Escape double quotes
        .replace(/\n/g, '\\n')   // Escape newlines
        .replace(/\r/g, '\\r')   // Escape carriage returns
        .replace(/\t/g, '\\t');  // Escape tabs
};

// Escape the notes field
$input.item.json.notes = escapeStringForJson($input.item.json.notes);

return $input.item;

// Get the input data for the first item
const inputData = $input.first().json;
console.log("Input Data:", inputData); // Log input for debugging

// Access the message string from the correct path within the input structure.
// The input comes from the "Build Response Payload" node, which structures data under 'results'.
// Use optional chaining (?.) for safety in case the structure is not as expected.
let message = inputData.results?.[0]?.result;

// Check if the message was found and is a string
if (typeof message !== 'string') {
    console.error("Could not find the message string at inputData.results[0].result or it's not a string. Input:", inputData);
    // Return an object with an empty message or an error indicator
    return { message: "" }; // Or potentially throw an error: throw new Error("Input message not found or not a string");
}

// Start cleaning the message string

// 1. Replace the literal string "\\n" (backslash followed by n) with a space.
// This handles the newline representation seen in the input screenshot.
let cleanedMessage = message.replace(/\\n/g, ' ');

// 2. Remove spaces immediately surrounding colons (e.g., "Times : " becomes "Times:").
cleanedMessage = cleanedMessage.replace(/\s*:\s*/g, ':');

// 3. Replace sequences of multiple whitespace characters (including spaces from replaced \n)
// with a single space. Then, trim any leading or trailing whitespace from the result.
cleanedMessage = cleanedMessage.replace(/\s+/g, ' ').trim();

// Create the final output JSON object containing the cleaned message.
const output = {
    message: cleanedMessage
};

// Return the output object. This will be the output of the Code node.
return output;

