// Get the input date from the previous node
const inputDateStr = $input.first().json.currentDate;
const inputDate = new Date(inputDateStr);

// Move to the first day of the current month
inputDate.setDate(1);

// Step back one day to land in the previous month
inputDate.setDate(0);

// Extract previous month and year
const previousMonth = inputDate.getMonth() + 1; // Months are 0-based
const year = inputDate.getFullYear(); // This will reflect the correct year, even in January

return [
  {
    json: {
      previousMonth: previousMonth.toString().padStart(2, '0'), // e.g., "01", "12"
      year: year.toString()                                     // e.g., "2024"
    }
  }
];

const rows = items;

// Get column names from the first row
const headers = Object.keys(rows[0].json);

// Build header HTML
let headerHtml = headers.map(col => `<th>${col}</th>`).join('');

// Build rows
let bodyHtml = rows.map(row => {
  return `<tr>${headers.map(col => `<td>${row.json[col]}</td>`).join('')}</tr>`;
}).join('');

// Combine into one table
const tableHtml = `
<table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse;">
  <thead><tr>${headerHtml}</tr></thead>
  <tbody>${bodyHtml}</tbody>
</table>
`;

return [{ json: { table: tableHtml } }];

