function escapeHTML(text) {
  return String(text)
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;");
}

function formatVolume(volume) {
  const vol = parseFloat(volume);
  if (vol >= 1_000_000_000) return (vol / 1_000_000_000).toFixed(2) + 'B';
  if (vol >= 1_000_000) return (vol / 1_000_000).toFixed(2) + 'M';
  if (vol >= 1_000) return (vol / 1_000).toFixed(2) + 'K';
  return vol.toString();
}

function formatMoney(amount) {
  return parseFloat(amount).toLocaleString('en-US', {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  });
}

function calculateVolatility(coin) {
  const high = parseFloat(coin.highPrice);
  const low = parseFloat(coin.lowPrice);
  const volatility = ((high - low) / low) * 100;
  return volatility.toFixed(2);
}

function calculateSpread(coin) {
  const ask = parseFloat(coin.askPrice);
  const bid = parseFloat(coin.bidPrice);
  const spread = ((ask - bid) / bid) * 100;
  return spread.toFixed(4);
}

function calculateMarketComparison(coin, avgMarketChange) {
  const coinChange = parseFloat(coin.priceChangePercent);
  const comparison = coinChange - avgMarketChange;
  return comparison.toFixed(2);
}

function formatActivity(count) {
  return count.toLocaleString('en-US');
}

function calculateMomentum(coin) {
  const current = parseFloat(coin.lastPrice);
  const weighted = parseFloat(coin.weightedAvgPrice);
  return ((current - weighted) / weighted * 100).toFixed(2);
}

function estimateMarketCap(coin) {
  return parseFloat(coin.lastPrice) * parseFloat(coin.quoteVolume);
}

function formatCoinWithAnalytics(coin, avgMarketChange) {
  const change = parseFloat(coin.priceChangePercent);
  const arrow = change > 0 ? 'üî∫' : 'üîª';
  const volatility = calculateVolatility(coin);
  const spread = calculateSpread(coin);
  const marketComparison = calculateMarketComparison(coin, avgMarketChange);
  const momentum = calculateMomentum(coin);

  const comparisonEmoji = marketComparison > 0 ? '‚≠ê' : '‚¨áÔ∏è';
  const momentumEmoji = parseFloat(momentum) > 0 ? 'üîº' : 'üîΩ';

  const timeFrameHours = (coin.closeTime - coin.openTime) / (1000 * 60 * 60);

  return `<b>${escapeHTML(coin.symbol)}</b>\n` +
         `${arrow} Change: ${escapeHTML(change.toFixed(2))}% (${timeFrameHours.toFixed(0)}h)\n` +
         `üí∞ Current: $${formatMoney(coin.lastPrice)}\n` +
         `üìä Range: $${formatMoney(coin.lowPrice)} - $${formatMoney(coin.highPrice)}\n` +
         `üìà Volatility: ${volatility}%\n` +
         `üîÑ Volume: ${escapeHTML(formatVolume(coin.volume))} | $${formatMoney(coin.quoteVolume)}\n` +
         `‚öñÔ∏è Bid-Ask Spread: ${spread}%\n` +
         `${comparisonEmoji} vs Market Avg: ${marketComparison}%\n` +
         `${momentumEmoji} Momentum: ${momentum}%\n` +
         `üî¢ Trades: ${formatActivity(coin.count)}\n\n`;
}

function calculateMarketStats(coins) {
  const totalVolume = coins.reduce((sum, coin) => sum + parseFloat(coin.quoteVolume), 0);
  const averageChange = coins.reduce((sum, coin) => sum + parseFloat(coin.priceChangePercent), 0) / coins.length;
  const mostVolatile = [...coins].sort((a, b) => calculateVolatility(b) - calculateVolatility(a))[0];
  const mostTraded = [...coins].sort((a, b) => parseFloat(b.quoteVolume) - parseFloat(a.quoteVolume))[0];
  const leastSpread = [...coins].sort((a, b) => calculateSpread(a) - calculateSpread(b))[0];

  const topByVolume = [...coins]
    .sort((a, b) => parseFloat(b.quoteVolume) - parseFloat(a.quoteVolume))
    .slice(0, 3);

  return {
    totalVolume,
    averageChange,
    mostVolatile,
    mostTraded,
    leastSpread,
    topByVolume
  };
}

const now = new Date();
const dateString = now.toISOString().replace('T', ' ').split('.')[0] + ' UTC';
const rawData = items[0].json;

const binanceData = Array.isArray(rawData) ? rawData : [];
const usdcPairs = binanceData.filter(coin => coin.symbol.endsWith('USDC'));

// Filter only for Solana, Bitcoin, Ethereum
const relevantSymbols = ['SOLUSDC', 'BTCUSDC', 'ETHUSDC'];
const filteredCoins = usdcPairs.filter(coin => relevantSymbols.includes(coin.symbol));

// Calculate market cap for each coin
filteredCoins.forEach(coin => {
  coin.estimatedMarketCap = estimateMarketCap(coin);
});

const marketStats = calculateMarketStats(filteredCoins);
const avgMarketChange = marketStats.averageChange;

const gainers = filteredCoins
  .filter(c => parseFloat(c.priceChangePercent) > 0)
  .sort((a, b) => parseFloat(b.priceChangePercent) - parseFloat(a.priceChangePercent));

const losers = filteredCoins
  .filter(c => parseFloat(c.priceChangePercent) < 0)
  .sort((a, b) => parseFloat(a.priceChangePercent) - parseFloat(b.priceChangePercent));

// Build message
let summary = `<b>üìä Crypto Market Summary ‚Äî ${escapeHTML(dateString)}</b>\n\n`;

summary += `<b>üåê Market Overview (BTC, ETH, SOL)</b>\n` +
           `Average Change: ${avgMarketChange.toFixed(2)}%\n` +
           `24h Volume: $${formatMoney(marketStats.totalVolume)}\n` +
           `Most Volatile: ${marketStats.mostVolatile.symbol} (${calculateVolatility(marketStats.mostVolatile)}%)\n` +
           `Most Liquid: ${marketStats.leastSpread.symbol} (${calculateSpread(marketStats.leastSpread)}% spread)\n\n`;

summary += `<b>üíπ Top by Volume</b>\n`;
marketStats.topByVolume.forEach(coin => {
  summary += `${coin.symbol}: $${formatMoney(coin.quoteVolume)} | ${coin.priceChangePercent}%\n`;
});
summary += `\n`;

if (gainers.length) {
  summary += `<b>üìà Gainers</b>\n\n`;
  summary += gainers.map(coin => formatCoinWithAnalytics(coin, avgMarketChange)).join('');
}

if (losers.length) {
  summary += `<b>üìâ Losers</b>\n\n`;
  summary += losers.map(coin => formatCoinWithAnalytics(coin, avgMarketChange)).join('');
}

const chunks = [];
let current = "";
summary.split(/\n/g).forEach(line => {
  const lineWithBreak = line + "\n";
  if ((current + lineWithBreak).length > 4000) {
    chunks.push({ json: { data: current.trim() } });
    current = lineWithBreak;
  } else {
    current += lineWithBreak;
  }
});

if (current.trim()) {
  chunks.push({ json: { data: current.trim() } });
}

return chunks;
