parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Amazon Bestsellers - Smartphones & Basic Mobiles",
  "type": "object",
  "properties": {
    "category": {
      "type": "string",
      "example": "Smartphones & Basic Mobiles"
    },
    "description": {
      "type": "string",
      "example": "Our most popular products based on sales. Updated frequently."
    },
    "page": {
      "type": "string",
      "example": "Page 1 of 2"
    },
    "bestsellers": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "rank": {
            "type": "integer",
            "example": 1
          },
          "title": {
            "type": "string",
            "example": "OnePlus Nord CE4 (Dark Chrome, 8GB RAM, 256GB Storage)"
          },
          "image": {
            "type": "string",
            "format": "uri",
            "example": "https://images-eu.ssl-images-amazon.com/images/I/61g1pqSjAhL._AC_UL300_SR300,200_.jpg"
          },
          "rating": {
            "type": "object",
            "properties": {
              "stars": {
                "type": "number",
                "example": 4.2
              },
              "total_ratings": {
                "type": "integer",
                "example": 8051
              }
            },
            "required": ["stars", "total_ratings"]
          },
          "offer": {
            "type": "string",
            "example": "1 offer from ₹23,998.00"
          },
          "product_url": {
            "type": "string",
            "format": "uri",
            "example": "https://www.amazon.in/Oneplus-Nord-Chrome-256GB-Storage/dp/B0CX5BZXLF/"
          }
        },
        "required": ["rank", "title", "image", "rating", "offer", "product_url"]
      }
    }
  },
  "required": ["category", "description", "page", "bestsellers"]
}
