// Accedemos al campo "output" que contiene un string JSON anidado
let rawText = $json.output;

if (!rawText || typeof rawText !== 'string' || rawText.trim() === '') {
    throw new Error("Error Cr√≠tico: No se recibi√≥ texto v√°lido (string no vac√≠o) en el campo 'output'.");
}

// Limpiar delimitadores tipo ```json y ```
rawText = rawText.trim();
rawText = rawText.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/, '').trim();

// Envolver en llaves si no las tiene
if (!rawText.startsWith('{')) {
    rawText = '{' + rawText;
}
if (!rawText.endsWith('}')) {
    rawText = rawText + '}';
}

// Truncar por si hay texto basura despu√©s del cierre
const lastBrace = rawText.lastIndexOf("}");
if (lastBrace !== -1) {
    rawText = rawText.slice(0, lastBrace + 1).trim();
}

console.log("Texto limpio para parsear (Inicial):", rawText);

try {
    const parsed = JSON.parse(rawText);

    if (typeof parsed !== 'object' || parsed === null) {
        throw new Error("El texto parseado no es un objeto JSON v√°lido.");
    }

    return { json: parsed };
} catch (error) {
    console.error("Error al parsear JSON:", error.message);
    console.error("Texto recibido:", rawText);
    throw new Error(`Error al parsear el campo 'output': ${error.message}`);
}
