// Clean and prepare HTML for API request
const html = $input.item.json.data;
// Trim if too large (optional)
const trimmedHtml = html.length > 100000 ? html.substring(0, 100000) : html;

return {
  json: {
    apiRequest: {
      document: {
        type: "HTML",
        content: trimmedHtml
      },
      encodingType: "UTF8"
    }
  }
}
