const inputText = $('When Executed by Another Workflow').first().json.output || "";
const lines = inputText.split("\n");

let chunks = [];
let currentChunk = "";

for (const line of lines) {
  // Check if adding the line would exceed the 1000 character limit
  if ((currentChunk + line + "\n").length > 1000) {
    // Push current chunk if not empty
    if (currentChunk) chunks.push(currentChunk.trim());
    // Start new chunk with current line
    currentChunk = line + "\n";
  } else {
    // Add line to current chunk
    currentChunk += line + "\n";
  }
}

// Push the final chunk if there’s remaining content
if (currentChunk) chunks.push(currentChunk.trim());

// Return each chunk as a separate item
return { output:chunks.map(chunk => ({ chunk } ))};

// free-text input
const text = $input.first().json.output ?? "";

/* media extensions grouped by type */
const IMG = ['jpg','jpeg','png','gif','bmp','webp','tif','tiff'];
const AUD = ['mp3','wav','m4a','flac','ogg','aac','opus'];
const VID = ['mp4','mov','mkv','webm','avi','mpeg','mpg'];

/* one regex that grabs any media URL (no lazy quantifier, so the whole link is kept) */
const re = /(https?:\/\/[^\s"'<>]+\.(?<ext>[A-Za-z0-9]{2,5}))(?:\?[^\s"'<>]*)?/gi;

const seen  = new Set();
const files = [];

for (const m of text.matchAll(re)) {
  const url = m[1];
  if (seen.has(url)) continue;          // de-dupe
  seen.add(url);

  const ext = (m.groups.ext || '').toLowerCase();

  let type = 'other';
  if (IMG.includes(ext))  type = 'image';
  else if (AUD.includes(ext)) type = 'audio';
  else if (VID.includes(ext)) type = 'video';

  files.push({ url, type });
}

return [{ json: { files } }];
