function processPayload(items) {
  // Create a new array to store the processed items
  const processedItems = [];

  // Process each item in the input array
  for (const item of items) {
    try {
      // Extract the decryptedPayload string from the current item
      const decryptedPayloadString = item.json.decryptedPayload;

      // Parse the decryptedPayload string into a JavaScript object
      const decryptedPayloadObject = JSON.parse(decryptedPayloadString);

      // Extract the date from the data object
      const date = decryptedPayloadObject.data.date;

      // Extract the screen value
      const screen = decryptedPayloadObject.screen;

      // Extract the flow_token object
      const flow_token = decryptedPayloadObject.flow_token;

      // Create a new item with the extracted date and screen
      const newItem = {
        json: {
          date: date,
          screen: screen,
          flow_token: flow_token,
          // Optionally preserve original data
          originalPayload: item.json
        }
      };

      // Add the processed item to our array
      processedItems.push(newItem);
    } catch (error) {
      // If there's an error, create an item with error information
      processedItems.push({
        json: {
          error: error.message,
          originalItem: item.json
        }
      });
    }
  }

  return processedItems;
}

return processPayload(items);

const groupedAppointments = items.reduce((acc, { json: { appointment_date, start_time } }) => {
