const crypto = require("crypto");

const privateKey = `-----BEGIN PRIVATE KEY-----
[INSERT YOUR KEY HERE]
-----END PRIVATE KEY-----`;

// Convert input buffers
const encryptedAesKeyBuffer = Buffer.from($json.encryptedAesKey.data);
const initialVector = Buffer.from($json.initialVector.data);
const encryptedFlowData = Buffer.from($json.encryptedFlowData.data);

// Check if encrypted AES key, IV, and encrypted flow data exist
if (!encryptedAesKeyBuffer || !initialVector || !encryptedFlowData) {
  throw new Error("Missing required data (encrypted AES key, IV, or flow data)");
}

// Decrypt AES key using RSA
const decryptedKey = crypto.privateDecrypt(
  {
    key: privateKey,
    padding: crypto.constants.RSA_PKCS1_OAEP_PADDING,
    oaepHash: "sha256",
  },
  encryptedAesKeyBuffer
);

// Ensure AES key is exactly 16 bytes (AES-128 requires it)
const aesKey = decryptedKey.slice(0, 16);
if (aesKey.length !== 16) {
  throw new Error("Invalid AES Key length");
}

// Handle initialization vector (IV): If needed, flip the IV bits (standardize behavior)
const standardizedIv = Buffer.from(initialVector);
if (standardizedIv.length !== 16) {
  throw new Error("Invalid IV length, must be 16 bytes");
}

// Extract the last 16 bytes as the authentication tag (GCM uses 16-byte tags)
const authTag = encryptedFlowData.slice(-16);
const encryptedDataWithoutTag = encryptedFlowData.slice(0, -16);

// AES Decryption
const decipher = crypto.createDecipheriv("aes-128-gcm", aesKey, standardizedIv);
decipher.setAuthTag(authTag);

let decrypted;
try {
  decrypted = Buffer.concat([
    decipher.update(encryptedDataWithoutTag),
    decipher.final(),
  ]);
} catch (error) {
  throw new Error("Decryption failed: " + error.message);
}

return [{ 
  decryptedPayload: decrypted.toString("utf-8"),
  aesKey: aesKey.toString("base64")
}];

