const ProjectLeads = {
  "alpha_backend": {
    "dev": ["dev1@example.com", "dev2@example.com"],
    "qa": ["qa1@example.com", "qa2@example.com"]
  },
  "beta_webapp": {
    "dev": ["dev3@example.com", "dev4@example.com"],
    "qa": ["qa3@example.com", "qa4@example.com"]
  },
  "gamma_mobile": {
    "dev": ["dev5@example.com", "dev6@example.com"],
    "qa": ["qa5@example.com", "qa6@example.com", "qa7@example.com"]
  },
  "delta_api": {
    "dev": ["dev7@example.com", "dev8@example.com", "dev9@example.com"],
    "qa": ["qa8@example.com", "qa9@example.com", "qa10@example.com"]
  },
  "epsilon_service": {
    "dev": ["dev10@example.com"],
    "qa": ["qa11@example.com", "qa12@example.com"]
  },
  "zeta_scraper": {
    "dev": ["dev11@example.com"],
    "qa": ["qa13@example.com", "qa14@example.com"]
  },
  "theta_ui": {
    "dev": ["dev12@example.com", "dev13@example.com"],
    "qa": ["qa15@example.com", "qa16@example.com"]
  },
  "iota_backend": {
    "dev": ["dev14@example.com", "dev15@example.com"],
    "qa": ["qa17@example.com", "qa18@example.com"]
  },
  "kappa_admin": {
    "dev": ["dev16@example.com", "dev17@example.com"],
    "qa": ["qa19@example.com", "qa20@example.com"]
  }
};

// Define the GlobalList
const GlobalList = [
  "admin1@example.com",
  "admin2@example.com",
  "admin3@example.com"
];

// Retrieve the project name from the input data and convert it to lowercase
const fullName = $('Merge').first().json.body.project.path_with_namespace.toLowerCase();
const projectName = fullName.split('/')[1];

let emails = [];

if (projectName && ProjectLeads[projectName]) {
  // Extract the emails for the given project
  const projectEmails = [
    ...ProjectLeads[projectName].dev,
    ...ProjectLeads[projectName].qa
  ];

  // Combine project-specific emails with GlobalList
  emails = [...projectEmails, ...GlobalList];
} else {
  // Default to GlobalList only if project name is not found or is undefined
  emails = [...GlobalList];
}

// Handle sender email replacement
const senderemail = $('Merge').first().json.body.object_attributes.last_commit.author.email;
const oldEmail = "86149715+user@users.noreply.github.com";
const newEmail = "user@example.com";
const senderEmail = senderemail.replace(oldEmail, newEmail);

// Add senderEmail to emails list
emails.push(senderEmail);

// Remove duplicate emails
emails = [...new Set(emails)];

// Join all emails into a single string
const emailsString = emails.join(", ");

// Return the result
return {
  json: {
    emails: emailsString
  }
};

