assignments.value:
=# Increase max body size for large file uploads
client_max_body_size 50000M;

# Proxy headers
proxy_set_header Host              $http_host;
proxy_set_header X-Real-IP         $remote_addr;
proxy_set_header X-Forwarded-For   $proxy_add_x_forwarded_for;
proxy_set_header X-Forwarded-Proto $scheme;

# WebSocket support
proxy_http_version 1.1;
proxy_set_header   Upgrade    $http_upgrade;
proxy_set_header   Connection "upgrade";

# Timeouts
proxy_read_timeout 600s;
proxy_send_timeout 600s;
send_timeout       600s;

# Additional optimizations
proxy_buffering off;
proxy_buffer_size 128k;
proxy_buffers 4 256k;
proxy_busy_buffers_size 256k;
proxy_temp_file_write_size 256k;
proxy_connect_timeout 600s;

=server_name  office.{{ $('API').item.json.body.domain }};

# static files
 location ^~ /browser {
   proxy_pass https://office.{{ $('API').item.json.body.domain }};
   proxy_set_header Host $host;
 }


 # WOPI discovery URL
 location ^~ /hosting/discovery {
   proxy_pass https://office.{{ $('API').item.json.body.domain }};
   proxy_set_header Host $host;
 }


 # Capabilities
 location ^~ /hosting/capabilities {
   proxy_pass https://office.{{ $('API').item.json.body.domain }};
   proxy_set_header Host $host;
 }


 # main websocket
 location ~ ^/cool/(.*)/ws$ {
   proxy_pass https://office.{{ $('API').item.json.body.domain }};
   proxy_set_header Upgrade $http_upgrade;
   proxy_set_header Connection "Upgrade";
   proxy_set_header Host $host;
   proxy_read_timeout 36000s;
 }


 # download, presentation and image upload
 location ~ ^/(c|l)ool {
   proxy_pass https://office.{{ $('API').item.json.body.domain }};
   proxy_set_header Host $host;
 }

 # Admin Console websocket
 location ^~ /cool/adminws {
   proxy_pass https://office.{{ $('API').item.json.body.domain }};
   proxy_set_header Upgrade $http_upgrade;
   proxy_set_header Connection "Upgrade";
   proxy_set_header Host $host;
   proxy_read_timeout 36000s;
 }

parameters.jsCode:
const domain = $('API').item.json.body.domain;

const parts = domain.split('.');

let subDomain = '';
let mainDomain = domain;

if (parts.length > 2) {
    subDomain = parts[0]; 
    mainDomain = parts.slice(1).join('.');
}

return {
  json: {
    subDomain: subDomain,
    mainDomain: mainDomain
  }
};

try {
  if ($json.stdout === 'success') {
    return {
      json: {
        status: 'success',
        message: '',
        data: '',
      }
    };
  }

  const parsedData = JSON.parse($json.stdout);

  return {
    json: {
      status: parsedData.status === 'error' ? 'error' : 'success',
      message: parsedData.message || (parsedData.status === 'error' ? 'An error occurred' : ''),
      data: parsedData || '',
    }
  };

} catch (error) {
  return {
    json: {
      status: 'error',
      message: $json.stdout??$json.error,
      data: '',
    }
  };
}
