function normalizeName(name) {
  if (!name) {
    return null
  }
    return name.toLowerCase().replace(/[\s&,.™®]/g, '');
}

function isWebsiteBelongsToCompany(url, sellerName, tradeName) {
    const websiteName = extractWebsiteName(url);
    console.log("websiteName: " + websiteName);
    if (!websiteName) return false;

    const normalizedWebsiteName = websiteName.toLowerCase();
    const normalizedSellerName = normalizeName(sellerName);
    const normalizedTradeName = normalizeName(tradeName);

    return normalizedWebsiteName.includes(normalizedSellerName) || 
           normalizedWebsiteName.includes(normalizedTradeName) ||
           normalizedSellerName.includes(normalizedWebsiteName) ||
           normalizedTradeName.includes(normalizedWebsiteName);
}

