parameters.functionCode:
// Extract and standardize Shopify order data
const eventData = $input.item.json;
const orderData = eventData.body; // This is the actual Shopify order object

// Extract customer info while preserving format
let customerName = '';
if (orderData.shipping_address && orderData.shipping_address.name) {
  customerName = orderData.shipping_address.name;
} else if (orderData.billing_address && orderData.billing_address.name) {
  customerName = orderData.billing_address.name;
} else if (orderData.customer) {
  // Concatenate first and last name, handling cases where one might be missing
  const firstName = orderData.customer.first_name || '';
  const lastName = orderData.customer.last_name || '';
  customerName = `${firstName} ${lastName}`.trim();
}

// Ensure line_items exists and is an array
const rawLineItems = Array.isArray(orderData.line_items) ? orderData.line_items : [];

// Transform to standardized format
const standardizedOrder = {
  orderId: orderData.id || '',
  orderNumber: orderData.order_number || '',
  created_at: orderData.created_at || new Date().toISOString(),
  processed: false, // Defaulting as per your desired structure
  processed_at: '',
  customer: {
    name: customerName,
    email: orderData.email || '',
    phone: (orderData.shipping_address && orderData.shipping_address.phone) ||
           (orderData.billing_address && orderData.billing_address.phone) || 
           (orderData.customer && orderData.customer.phone) || 
           ''
  },
  shippingAddress: {
    name: (orderData.shipping_address && orderData.shipping_address.name) || customerName,
    address1: (orderData.shipping_address && orderData.shipping_address.address1) || '',
    address2: (orderData.shipping_address && orderData.shipping_address.address2) || '',
    city: (orderData.shipping_address && orderData.shipping_address.city) || '',
    province: (orderData.shipping_address && orderData.shipping_address.province) || '',
    zip: (orderData.shipping_address && orderData.shipping_address.zip) || '',
    country: (orderData.shipping_address && orderData.shipping_address.country_code) || ''
  },
  lineItems: rawLineItems.map(item => ({
    id: item.id || '',
    productId: item.product_id || '',
    variantId: item.variant_id || '',
    sku: item.sku || '', 
    name: item.name || item.title || '',
    quantity: item.quantity || 1,
    price: item.price || '0.00',
    supplier: null,
    fulfillment_status: item.fulfillment_status || 'unfulfilled',
    tracking_number: null
  })),
  totalPrice: orderData.total_price || '0.00',
  currency: orderData.currency || 'USD',
  json: JSON.stringify(orderData) // Store the full original order for reference
};

// Convert objects to strings for Google Sheets
standardizedOrder.customer = JSON.stringify(standardizedOrder.customer);
standardizedOrder.shippingAddress = JSON.stringify(standardizedOrder.shippingAddress);
standardizedOrder.lineItems = JSON.stringify(standardizedOrder.lineItems);

return {json: standardizedOrder};

