// Calculate a volatility score based on price changes
const item = $input.item.json;

// Extract price change percentages
const priceChange24h = item.price_change_percentage_24h || 0;
const priceChange7d = item.price_change_percentage_7d_in_currency || 0;
const priceChange30d = item.price_change_percentage_30d_in_currency || 0;

// Calculate absolute values to measure volatility regardless of direction
const absChange24h = Math.abs(priceChange24h);
const absChange7d = Math.abs(priceChange7d);
const absChange30d = Math.abs(priceChange30d);

// Calculate a weighted volatility score (more weight to recent changes)
const volatilityScore = (absChange24h * 0.5) + (absChange7d * 0.3) + (absChange30d * 0.2);

// Calculate market cap to volume ratio (higher means less liquid/more volatile)
const marketCapToVolumeRatio = item.market_cap / (item.total_volume || 1);

// Calculate price to ATH ratio (close to 1 means near all-time high)
const priceToAthRatio = item.current_price / (item.ath || item.current_price);

// Calculate a composite market score
const marketScore = (
  (volatilityScore * 0.4) + 
  (Math.min(marketCapToVolumeRatio, 10) * 0.3) + 
  ((1 - priceToAthRatio) * 30) // Invert so coins further from ATH get higher scores
);

// Add calculated metrics to the item
item.volatilityScore = parseFloat(volatilityScore.toFixed(2));
item.marketCapToVolumeRatio = parseFloat(marketCapToVolumeRatio.toFixed(2));
item.priceToAthRatio = parseFloat(priceToAthRatio.toFixed(4));
item.marketScore = parseFloat(marketScore.toFixed(2));

// Add a timestamp for the analysis
item.analyzedAt = new Date().toISOString();

return {json: item};

// Create a formatted summary for each coin
const item = $input.item.json;

// Format dollar amounts nicely
const formatCurrency = (value) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  }).format(value);
};

// Format percentages nicely
const formatPercent = (value) => {
  return `${value > 0 ? '+' : ''}${value.toFixed(2)}%`;
};

// Create a detailed analysis report
const analysisReport = {
  coin: item.name,
  symbol: item.symbol.toUpperCase(),
  currentPrice: formatCurrency(item.current_price),
  marketCap: formatCurrency(item.market_cap),
  volume24h: formatCurrency(item.total_volume),
  priceChanges: {
    "24h": formatPercent(item.price_change_percentage_24h),
    "7d": formatPercent(item.price_change_percentage_7d_in_currency),
    "30d": formatPercent(item.price_change_percentage_30d_in_currency)
  },
  marketMetrics: {
    volatilityScore: item.volatilityScore,
    marketCapToVolumeRatio: item.marketCapToVolumeRatio,
    priceToAthRatio: item.priceToAthRatio,
    marketScore: item.marketScore
  },
  analysis: {
    priceAction: item.priceAction,
    signal: item.signal,
    riskRating: item.riskRating,
    recommendation: item.recommendation,
    investmentStrategy: item.investmentStrategy
  },
  timestamp: item.analyzedAt
};

// Generate a natural language summary
const summary = `
## ${item.name} (${item.symbol.toUpperCase()}) Analysis

Current Price: ${formatCurrency(item.current_price)} (${formatPercent(item.price_change_percentage_24h)} 24h)\n\n

Market Signal: **${item.signal}** - *${item.priceAction}*
Risk Rating: **${item.riskRating}**\n\n

**Recommendation:**
${item.recommendation}

**Investment Strategy:**
${item.investmentStrategy}

---
*Analysis based on volatility score of ${item.volatilityScore} and market score of ${item.marketScore}*
`;

// Return both structured data and a human-readable summary
return {
  json: {
    ...item,
    analysisReport,
    summary
  }
};

// Create a portfolio summary based on all coin analyses
const allCoins = $input.all();

// Extract all analyzed coins
const analyzedCoins = allCoins.map(coin => coin.json);

// Count signals by type
const signalCounts = {
  BUY: 0,
  SELL: 0,
  HOLD: 0,
  NEUTRAL: 0
};

// Count risk ratings
const riskCounts = {
  "High Risk": 0,
  "Medium Risk": 0,
  "Low Risk": 0,
  "Unknown Risk": 0
};

// Calculate portfolio metrics
analyzedCoins.forEach(coin => {
  // Count signals
  if (coin.signal) {
    signalCounts[coin.signal] = (signalCounts[coin.signal] || 0) + 1;
  }

  // Count risk ratings
  if (coin.riskRating) {
    riskCounts[coin.riskRating] = (riskCounts[coin.riskRating] || 0) + 1;
  }
});

// Sort coins by market score (highest first)
const sortedCoins = [...analyzedCoins].sort((a, b) => b.marketScore - a.marketScore);

// Get top recommendation
const topOpportunity = sortedCoins[0];

// Generate portfolio summary markdown
const portfolioSummary = `# Cryptocurrency Market Analysis

## Market Overview
**Analyzed Coins:** ${analyzedCoins.length}  
**Buy Signals:** ${signalCounts.BUY}  
**Sell Signals:** ${signalCounts.SELL}  
**Hold Signals:** ${signalCounts.HOLD}  
**Neutral Signals:** ${signalCounts.NEUTRAL}  

## Risk Distribution
**High Risk Assets:** ${riskCounts["High Risk"]}  
**Medium Risk Assets:** ${riskCounts["Medium Risk"]}  
**Low Risk Assets:** ${riskCounts["Low Risk"]}  

## Top Opportunity  
${topOpportunity ? `**${topOpportunity.name} (${topOpportunity.symbol.toUpperCase()})**  
${topOpportunity.summary}` : "No opportunities found"}

## Detailed Coin Analyses  
${sortedCoins.map(coin => `**${coin.name} (${coin.symbol})**  
${coin.summary}`).join('\n\n')}

*Analysis generated on ${new Date().toLocaleString()}*`;

return [
  {
    json: {
      portfolioSummary,
      individualAnalyses: analyzedCoins.map(coin => coin.analysisReport),
      marketSignals: signalCounts,
      riskDistribution: riskCounts,
      timestamp: new Date().toISOString()
    }
  }
];
