return [
  {
    json: {
      url: (() => {
        let input = $json.chatInput.trim().toLowerCase();
        if (!input.startsWith('http')) input = 'https://' + input;
        return input;
      })()
    }
  }
];

const html = $json.data || '';

// Remove scripts, styles, and extract visible text
const cleanedText = html
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '')
  .replace(/<[^>]*>/g, '') // strip all HTML tags
  .replace(/\s+/g, ' ')
  .trim();

// Preview: first 400 chars of visible content
const previewText = cleanedText.slice(0, 400);

// Lowercased text for language-agnostic JS block detection
const lowerText = cleanedText.toLowerCase();

// Common JS-related blocking messages (EN + NL + DE)
const jsBlockIndicators = [
  'enable javascript',
  'javascript is required',
  'please enable javascript',
  'javascript moet ingeschakeld zijn',
  'schakel javascript in',
  'javascript erforderlich'
];

// Scan for any of the indicators
const jsWarningDetected = jsBlockIndicators.some(phrase => lowerText.includes(phrase));

return [
  {
    json: {
      visibleTextLength: cleanedText.length,
      previewText,
      hasH1: /<h1[\s>]/i.test(html),
      hasH2: /<h2[\s>]/i.test(html),
      hasH3: /<h3[\s>]/i.test(html),
      hasMetaDescription: /<meta[^>]+name=["']description["']/i.test(html),
      hasOpenGraph: /<meta[^>]+property=["']og:/i.test(html),
      hasStructuredData: /<script[^>]*type=["']application\/ld\+json["']/i.test(html),
      hasNoscript: /<noscript[\s>]/i.test(html),
      jsWarning: jsWarningDetected,
      robotsTxt: $('Sanitize Website URL').first().json.url.replace(/\/$/, '') + '/robots.txt'
    }
  }
];
