function tranformContent(content) {
  return [].concat(content).map(content => {
    if (typeof content === 'string') {
      return { type: "input_text", text: content };
    }
    return {
      type: getInputType(content.type),
      [content.type]: content[content.type].url
    }
  })
};

function getInputType(type) {
  if (type === 'image_url') return 'input_image';
  if (type === 'file_url') return 'input_file';
  return 'input_text';
}

const input = $input.item.json.body.messages.map(message => ({
  role: message.role,
  content: tranformContent(message.content)
}));

return { input };

return {
  "id": $json.id,
  "object": "chat.completion",
  "created": $json.created_at,
  "model": $json.model,
  "choices": $json.output.flatMap((item,idx) => (
    item.content.map((content) => ({
      index: idx,
      finish_reason: "stop",
      "message": {
        "annotations": content.annotations,
        "content": content.text,
        "refusal": null,
        "role": item.role
      }
    }))
  )),
  "usage": {
    "completion_tokens": $json.usage.output_tokens,
    "completion_tokens_details": $json.usage.output_tokens_details,
    "prompt_tokens": $json.usage.input_tokens_details,
    "prompt_tokens_details": $json.usage.input_tokens_details,
    "total_tokens": $json.usage.total_tokens
  },
  "service_tier": $json.service_tier,
  "system_fingerprint": $json.id
}

const items = $input.item.json.data.split(/^\n/mg);
const events = items.map(item => {
  const [event, data] = item.split('\n');
  return {
    event: event?.split(':')[1]?.trim() ?? null,
    data: data?.substring(data.indexOf(':')+1,data.length)?.trim() ?? null
  }
});

const done = events.find(item => item.event === 'response.completed');
const res = JSON.parse(done.data).response;

const chunk = {
  id: res.id,
  object:"chat.completion.chunk",
  created: res.created_at,
  model: res.model,
  service_tier: res.service_tier,
  system_fingerprint: res.id,
  choices: [{
    index: 0,
     delta: { content: res.output[0].content[0].text }
  }],
  "usage": {
    "completion_tokens": res.usage.output_tokens,
    "completion_tokens_details": res.usage.output_tokens_details,
    "prompt_tokens": res.usage.input_tokens_details,
    "prompt_tokens_details": res.usage.input_tokens_details,
    "total_tokens": res.usage.total_tokens
  }
};

const data = [
  `data: ${JSON.stringify(chunk)}`,
  `data: [DONE]`
].join('\n\n');

return { data };
