additionalFields.outputSchema:
{
  "type": "object",
  "properties": {
    "full_name": {
      "type": "string",
      "description": "The full name of the individual."
    },
    "current_or_last_title": {
      "type": "string",
      "description": "The job title next to the logo of the current or last employer."
    },
    "current_or_last_employer": {
      "type": "string",
      "description": "The name of the first company in the employment experience block."
    },
    "linkedin_company_url": {
      "type": "string",
      "description": "The LinkedIn URL of the first company in the employment experience block."
    },
    "location": {
      "type": "string",
      "description": "The location of the individual."
    },
    "number_of_connections": {
      "type": "integer",
      "description": "The number of connections the individual has."
    },
    "number_of_followers": {
      "type": "integer",
      "description": "The number of followers the individual has."
    },
    "about_section_text": {
      "type": "string",
      "description": "The text from the 'About' section."
    },
    "ai_interest_level": {
      "type": "string",
      "description": "The person's interest level in AI."
    },
    "seniority_level": {
      "type": "string",
      "description": "The seniority level of the person."
    },
    "technical_depth": {
      "type": "string",
      "description": "The technical depth of the person."
    },
    "icp_score": {
      "type": "integer",
      "description": "The ICP Score calculated based on AI interest, technical depth, and seniority level."
    }
  },
  "required": [
    "full_name",
    "current_or_last_title",
    "current_or_last_employer",
    "linkedin_company_url",
    "location",
    "number_of_connections",
    "number_of_followers",
    "about_section_text",
    "ai_interest_level",
    "seniority_level",
    "technical_depth",
    "icp_score"
  ],
