additionalFields.outputSchema:
{
  "type": "object",
  "properties": {
    "company_profile": {
      "type": "object",
      "properties": {
        "name": {
          "type": "string",
          "description": "Full company name including suffixes like Inc., LLC, etc."
        },
        "tagline": {
          "type": "string",
          "description": "Brand tagline or headline directly under company name."
        },
        "location": {
          "type": "object",
          "properties": {
            "city": {
              "type": "string"
            },
            "state": {
              "type": "string"
            },
            "country": {
              "type": "string"
            }
          },
          "required": [
            "city",
            "state",
            "country"
          ],
          "additionalProperties": false,
          "description": "Global headquarters location."
        },
        "overview": {
          "type": "string",
          "description": "Full 'About' section text of the company."
        },
        "website": {
          "type": "string",
          "description": "Primary website URL excluding social media links."
        }
      },
      "required": [
        "name",
        "tagline",
        "location",
        "overview",
        "website"
      ],
      "additionalProperties": false
    },
    "scale": {
      "type": "object",
      "properties": {
        "employee_count": {
          "type": "integer",
          "description": "Current employee count from LinkedIn 'X employees' metric."
        },
        "size_bracket": {
          "type": "string",
          "description": "Employee range bracket."
        }
      },
      "required": [
        "employee_count",
        "size_bracket"
      ],
      "additionalProperties": false
    },
    "classification": {
      "type": "object",
      "properties": {
        "is_automation_agency": {
          "type": "boolean",
          "description": "Boolean classification if the company is an automation agency."
        },
        "ai_focus_level": {
          "type": "string",
          "description": "AI implementation level based on company activities."
        },
        "technical_tier": {
          "type": "string",
          "description": "Overall technical capabilities."
        }
      },
      "required": [
        "is_automation_agency",
        "ai_focus_level",
        "technical_tier"
      ],
      "additionalProperties": false
    },
    "funding": {
      "type": "object",
      "properties": {
        "latest_round": {
          "type": "string",
          "description": "Most recent funding round."
        },
        "total_raised": {
          "type": "string",
          "description": "Total funding amount."
        },
        "investors": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Key investors."
        },
        "last_updated": {
          "type": "string",
          "description": "Last funding date."
        }
      },
      "required": [
        "latest_round",
        "total_raised",
        "investors",
        "last_updated"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "company_profile",
    "scale",
    "classification",
    "funding"
  ],
  "additionalProperties": false,
  "$schema": "http://json-schema.org/draft-07/schema#"
}
