const aiOutput = $json.output || "No data available.";
const date = new Date().toISOString().split('T')[0];

// Extract wallet using regex
const walletMatch = aiOutput.match(/Wallet:\s*(0x[a-fA-F0-9]{40})/);
const wallet = walletMatch ? walletMatch[1] : "Unknown Wallet";

// Convert \n to <br> for HTML formatting
const htmlFormatted = aiOutput
  .replace(/---/g, '<hr>') // Replace section breaks
  .replace(/\n{2,}/g, '<br><br>') // Extra line spacing between sections
  .replace(/\n/g, '<br>'); // Standard line breaks

// Wrap in basic HTML styling
const htmlBody = `
  <div style="font-family: Arial, sans-serif; font-size: 14px; line-height: 1.6; color: #222;">
    ${htmlFormatted}
  </div>
`;

const textBody = aiOutput;
const subject = `üõ°Ô∏è Aave DeFi Health Report ‚Äì ${date}`;

return [
  {
    json: {
      wallet,
      subject,
      htmlBody,
      textBody
    }
  }
];
