function safe(x) 
{
  return x.replaceAll(":","").replaceAll("/","|")
}

function findValidTargets(connections, sourceNode, potentialTargets) {
  const visited = new Set();
  const foundTargets = new Set();

  function dfs(node) {
    if (visited.has(node)) return;
    visited.add(node);

    if (potentialTargets.includes(node)) {
      foundTargets.add(node);
      return; // Optionally continue search if multiple paths to same target are needed
    }

    const nodeConnections = connections[node]?.main || [];
    for (const path of nodeConnections) {
      for (const next of path) {
        dfs(next.node);
      }
    }
  }

  dfs(sourceNode);
  return Array.from(foundTargets);
}

const nodes = []
for (const item of $input.all()) 
{
  let webhook = {
    name: item.json.name,
    id: item.json.id,
    webhooks: []
  }
  const webhook_call = item.json.nodes.filter(node =>node.type=='n8n-nodes-base.webhook');
  const webhook_resp = item.json.nodes.filter(node =>node.type=='n8n-nodes-base.respondToWebhook');
  const targets = webhook_resp.map(wr => wr.name);
  if(webhook_call.length)
  {
    for(const call of webhook_call)
    {
        const callName = call.name;
        if(call?.parameters?.responseMode == "responseNode")
        {
            const valid = findValidTargets(item.json.connections, callName, targets);
            call.responses = webhook_resp.filter(wr => valid.includes(wr.name))
        }
    }

      webhook.webhooks.push(...webhook_call);
      nodes.push(webhook);
  }
}

let s = `
swagger: "2.0"
info:
  title: N8N Instance API
  description: API description in Markdown.
  version: 1.0.0

host: ${$('Get Swagger').first().json?.headers?.host || 'n8n.instance.com'}

basePath: /webhook
schemes:
  - https
paths:
`

for(const node of nodes)
{
    const used_path = {};
    for(const w of node.webhooks)
    {
      let path = '';
      if(!(w?.parameters?.path in used_path))
      {
        path = w?.parameters?.path;
        used_path[path] = true;
      }

      let produces = "application/json"
      let code = 200;
      if(w?.responses?.length)
      {
          for(const r of w.responses)
          {
              switch(r?.parameters?.respondWith)
              {
                case 'text':
                  produces ='text/plain';
                break
                case 'redirect':
                  produces = 'redirect';
                  code = 301
                break
                case 'json':
                  produces = 'application/json';
                break
              }
          }
      }
      let parameters = []
      if(w?.notes)
      {
        console.log({notes: w.notes})
        const params = w?.notes?.split('\n').filter(p => p.startsWith('@'));
        for(const p of params)
        {
          const parts = p.split(' ');
          const [position, name, type] = parts;
          const description = parts.slice(3).join(' ');
          console.log(p.split(' '))
          switch(position)
          {
            case '@query':
              parameters.push(`
        - name: ${name}
          in: ${position.replaceAll('@', '')}
          description: ${description || 'no description'}
          type: ${type}
          required: true
`)
            break
            case '@body':
              parameters.push(`
        - name: ${name}
          in: ${position.replaceAll('@', '')}
          description: ${description || 'no description'}
          schema:
            type: object
            required:
              - ${name}
            properties:
              ${name}:
                type: ${type}
`)
          }

        }

        if(parameters.length)
        {
          parameters.unshift(`
      parameters:
`)
        }
      }
      s+=`
  ${path ? `/${path}:` : ''}
    ${w?.parameters?.httpMethod?.toLowerCase() || 'get'} :
      summary: ${safe(w?.name) || safe(node.name)}.
      description: Related to workflow [${node.id}].
      tags:
        - ${safe(node.name)}
      ${parameters.join('\n')}
      produces:
        - ${produces}
      responses:
        ${code}:
          description: OK`
    }
}

return {json:{text: s, nodes}};

