const simplifiedResults = items.map(item => {
  return {
    json: {
      Keyword: item.json.Keyword,
      rank: item.json.rank
    }
  };
});

// Start the HTML table
let emailContent = "<table border='1' cellpadding='5' cellspacing='0' style='border-collapse: collapse;'>";

// Add table header
emailContent += "<tr><th>Keyword</th><th>Rank</th></tr>";

// Add table rows for each result
simplifiedResults.forEach(result => {
  emailContent += `<tr><td>${result.json.Keyword}</td><td>${result.json.rank}</td></tr>`;
});

// Close the table tag
emailContent += "</table>";

// Return the email content with the table
return [
  {
    json: {
      emailBody: emailContent
    }
  }
];

// Get all rows from input
const items = $input.all();

// Transform each row's 'Keyword' field
return items.map(item => {
  const keyword = item.json.Keyword || '';
  const transformedKeyword = keyword.replaceAll(' ', '+');

  return {
    json: {
      ...item.json,  // keep original data
      transformedKeyword
    }
  };
});

const html = $input.first().json.data;
const targetDomain = $('Reading Keywords').first().json.Domain; 


const regex = /<a[^>]+href="(http[^"]+)"[^>]*>/g;
let match;
let links = [];
while ((match = regex.exec(html)) !== null) {
  const url = match[1];
  if (
    url.startsWith("http") &&
    !url.includes("google.com") &&
    !url.includes("/search?") &&
    !url.includes("webcache")
  ) {
    links.push(url);
  }
}

// Deduplicate and trim links
links = [...new Set(links.map(link => link.trim()))];

// Try to find the rank (position) of your target domain
let rank = null;
let foundUrl = null;
for (let i = 0; i < links.length; i++) {
  if (links[i].includes(targetDomain)) {
    rank = i + 1; // 1-based position
    foundUrl = links[i];
    break;
  }
}

// Get current date and time
const now = new Date();
const dateTime = now.toLocaleString(); // e.g., "5/19/2025, 2:30:00 PM"

// Output result
return [
  {
    json: {
      row: $('Loop over Keywords').first().json.row_number,
      rank: rank || "Not Ranked",
      url: foundUrl || "N/A",
      totalResultsChecked: links.length,
      found: !!foundUrl,
      checkedAt: dateTime
    }
  }
];
