        else:  # Object/dict-like
            return {k: jsproxy_to_python(obj[k]) for k in obj.keys()}
    elif isinstance(obj, list):
        return [jsproxy_to_python(i) for i in obj]
    elif isinstance(obj, dict):
        return {k: jsproxy_to_python(v) for k, v in obj.items()}
