const jwt = require('jsonwebtoken');
const jwksClient = require('jwks-rsa');

const client = jwksClient({
  jwksUri: 'https://dev-abcdef.us.auth0.com/.well-known/jwks.json',
});

const config = {
  audience: 'https://dev-abcdef.us.auth0.com/api/v2/',
  issuer: 'https://dev-abcdef.us.auth0.com/',
  algorithms: ['RS256'],
};

function getKey(header, callback) {
  client.getSigningKey(header.kid, (err, key) => {
    if (err) return callback(err);
    callback(null, key.getPublicKey());
  });
}

function verifyToken(token) {
  return new Promise((res,rej) => {
    jwt.verify(
      token,
      getKey,
      config,
      (err, decoded) => {
        if (err) {
          rej('invalid token');
        } else {
          res(decoded);
        }
      }
    );
  });
}

const [_, token] = $json.headers.Authorization.split(' ');
const payload = await verifyToken(token);
return {
  ...$input.item.json,
  jwtPayload: payload
};

const jwt = require('jsonwebtoken');

const config = {
  audience: 'https://dev-abcdef.us.auth0.com/api/v2/',
  issuer: 'https://dev-abcdef.us.auth0.com/',
  algorithms: ['RS256'],
};

// Signing Cert can be found via
// applications > settings > advanced settings > certificates
const cert = `-----BEGIN CERTIFICATE-----
...
-----END CERTIFICATE-----`;

const [_, token] = $json.headers.Authorization.split(' ');

try {
  const payload = jwt.verify(token, cert, config);
  return {
    ...$input.item.json,
    jwtPayload: payload
  }
} catch (e) {
  throw new Error('Invalid Token!');
}
