// Code node in N8n
const inputData = $input.first().json;

// Clone the previous data before modifying it
const previous = {...inputData.previous};

// Change 1: Increase all numeric prices by $5 in the pricing array
if (previous?.pricing && Array.isArray(previous.pricing)) {
  previous.pricing.forEach(plan => {
    // Check if price is a numeric string
    if (plan.price && !isNaN(parseFloat(plan.price))) {
      // Add $5 to the price
      plan.price = (parseFloat(plan.price) + 5).toString();
    }
  });
}

// Change 2: Modify a feature in the first paid plan (if exists)
if (previous?.pricing && previous.pricing.length > 1) {
  const firstPaidPlan = previous.pricing.find(plan => 
    plan.price && plan.price !== "0" && !isNaN(parseFloat(plan.price)));

  if (firstPaidPlan && Array.isArray(firstPaidPlan.features) && firstPaidPlan.features.length > 1) {
    // Add "Premium" prefix to the second feature (if it exists)
    if (firstPaidPlan.features[1]) {
      firstPaidPlan.features[1] = "Premium " + firstPaidPlan.features[1];
    }
  }
}

// Change 3: Update the first FAQ answer (if exists)
if (previous?.faq && Array.isArray(previous.faq) && previous.faq.length > 0) {
  if (previous.faq[0] && previous.faq[0].answer) {
    // Add a sentence to the end of the answer
    previous.faq[0].answer += " For more information, contact our sales team.";
  }
}

// Return the data with current and modified previous
return [{
  json: {
    current: inputData.current,
    previous: previous
  }
}];

// N8n Code Node to compare previous and current data
// Input: $input.first().json contains previous and current data objects

function detectChanges() {
  const previous = $input.first().json.previous;
  const current = $input.first().json.current;

  // Initialize changes object with better structure for Markdown generation
  const changes = {
    detected: false,
    sections: {}, // Organized by section
    summary: {
      totalChanges: 0,
      timestamp: new Date().toISOString()
    }
  };

  // Helper function to add a change to the changes object
  function addChange(section, path, changeType, changeData) {
    // Initialize section if it doesn't exist
    if (!changes.sections[section]) {
      changes.sections[section] = {
        name: formatSectionName(section),
        changes: []
      };
    }

    // Format display path
    const displayPath = formatPath(path);

    // Add change to the section
    changes.sections[section].changes.push({
      displayPath,
      type: changeType,
      ...changeData
    });

    // Update global detected flag and counter
    changes.detected = true;
    changes.summary.totalChanges++;
  }

  // Helper function to format section name for display
  function formatSectionName(name) {
    // Convert camelCase to Title Case with spaces
    return name.replace(/([A-Z])/g, ' $1')
      .replace(/^./, str => str.toUpperCase())
      .trim();
  }

  // Helper function to format path for display
  function formatPath(path) {
    // Format the path elements to be more readable
    return path.replace(/\[([^\]]+)\]/g, ' ($1)');
  }

  // Helper function to compare primitive values
  function compareValues(prevVal, currVal, path) {
    if (prevVal !== currVal) {
      const section = path.split('.')[0];
      addChange(section, path, 'changed', {
        old: prevVal,
        new: currVal
      });
      return true;
    }
    return false;
  }

  // Helper function to compare arrays
  function compareArrays(prevArr, currArr, path, matchKey = null) {
    let changed = false;
    const section = path.split('.')[0];
    const additions = [];
    const deletions = [];

    // Check for deletions
    for (let i = 0; i < prevArr.length; i++) {
      const prevItem = prevArr[i];

      if (matchKey && typeof prevItem === 'object' && prevItem !== null) {
        // For arrays of objects with a specified match key (like planName or question)
        const matchFound = currArr.some(currItem => currItem[matchKey] === prevItem[matchKey]);
        if (!matchFound) {
          deletions.push(prevItem);
          changed = true;
        }
      } else {
        // For arrays of primitives
        if (!currArr.includes(prevItem)) {
          deletions.push(prevItem);
          changed = true;
        }
      }
    }

    // Check for additions
    for (let i = 0; i < currArr.length; i++) {
      const currItem = currArr[i];

      if (matchKey && typeof currItem === 'object' && currItem !== null) {
        // For arrays of objects with a specified match key
        const matchFound = prevArr.some(prevItem => prevItem[matchKey] === currItem[matchKey]);
        if (!matchFound) {
          additions.push(currItem);
          changed = true;
        }
      } else {
        // For arrays of primitives
        if (!prevArr.includes(currItem)) {
          additions.push(currItem);
          changed = true;
        }
      }
    }

    // Check for changes in matching objects
    if (matchKey) {
      for (let i = 0; i < currArr.length; i++) {
        const currItem = currArr[i];
        if (typeof currItem === 'object' && currItem !== null) {
          const matchingPrevItem = prevArr.find(prevItem => prevItem[matchKey] === currItem[matchKey]);
          if (matchingPrevItem) {
            // Compare the matching objects recursively
            const itemPath = `${path}[${matchKey}=${currItem[matchKey]}]`;
            compareObjects(matchingPrevItem, currItem, itemPath);
          }
        }
      }
    }

    if (changed) {
      addChange(section, path, 'array_changed', {
        additions: additions.length > 0 ? additions : null,
        deletions: deletions.length > 0 ? deletions : null
      });
    }

    return changed;
  }

  // Helper function to compare objects
  function compareObjects(prevObj, currObj, path = '') {
    if (!prevObj || !currObj) return false;

    const allKeys = new Set([...Object.keys(prevObj), ...Object.keys(currObj)]);
    let changed = false;

    for (const key of allKeys) {
      // Skip rawMarkdown as requested
      if (key === 'rawMarkdown') continue;

      const keyPath = path ? `${path}.${key}` : key;
      const section = keyPath.split('.')[0];
      const prevVal = prevObj[key];
      const currVal = currObj[key];

      // Handle missing keys
      if (!(key in prevObj)) {
        addChange(section, keyPath, 'added', {
          value: currVal
        });
        changed = true;
        continue;
      }

      if (!(key in currObj)) {
        addChange(section, keyPath, 'removed', {
          value: prevVal
        });
        changed = true;
        continue;
      }

      // Compare based on type
      if (Array.isArray(prevVal) && Array.isArray(currVal)) {
        // Special handling for specific array types
        if (key === 'pricing') {
          compareArrays(prevVal, currVal, keyPath, 'planName');
        } else if (key === 'features') {
          compareArrays(prevVal, currVal, keyPath);
        } else if (key === 'faq') {
          compareArrays(prevVal, currVal, keyPath, 'question');
        } else {
          compareArrays(prevVal, currVal, keyPath);
        }
      } else if (
        typeof prevVal === 'object' && prevVal !== null &&
        typeof currVal === 'object' && currVal !== null
      ) {
        compareObjects(prevVal, currVal, keyPath);
      } else {
        compareValues(prevVal, currVal, keyPath);
      }
    }

    return changed;
  }

  // Start comparison - removed the .output reference
  compareObjects(previous, current);

  return [{ json: {previous, current, changes} }];
}

// Execute the function and return the results
return detectChanges();

// N8n Code Node to generate Markdown from structured changes
// Input: $input.changes contains the comparison results from the improved comparison node
function generateChangelogMarkdown() {
  // Changed to directly access the changes property from the input
  const changes = $input.first().json.changes;

  if (!changes || !changes.detected) {
    return [{ json: { markdown: "# Changelog\n\nNo changes detected." }}];
  }

  // Start building the markdown
  // Getting the filename from the input for the title
  let fileNameWithoutExt = '';
  try {
    // Try to get the filename from the Loop Over Items node if available
    fileNameWithoutExt = $('Loop over each comparison URL').first().json[""][0];
  } catch (e) {
    // Fallback to a generic title if the loop node isn't available
    fileNameWithoutExt = "Website";
  }

  let markdown = `# ${fileNameWithoutExt} Changes\n\n`;

  // Process each section in the changes object
  Object.values(changes.sections).forEach(section => {
    markdown += `## ${section.name}\n\n`;

    // Process each change in this section
    section.changes.forEach(change => {
      switch (change.type) {
        case 'changed':
          markdown += `- **${change.displayPath}** changed from \`${formatValue(change.old)}\` to \`${formatValue(change.new)}\`\n`;
          break;

        case 'added':
          markdown += `- **${change.displayPath}** was added with value \`${formatValue(change.value)}\`\n`;
          break;

        case 'removed':
          markdown += `- **${change.displayPath}** was removed (previously \`${formatValue(change.value)}\`)\n`;
          break;

        case 'array_changed':
          markdown += `- **${change.displayPath}** has changes:\n`;

          // Handle additions
          if (change.additions && change.additions.length > 0) {
            markdown += `  - **Added**:\n`;
            change.additions.forEach(item => {
              markdown += `    - \`${formatValue(item)}\`\n`;
            });
          }

          // Handle deletions
          if (change.deletions && change.deletions.length > 0) {
            markdown += `  - **Removed**:\n`;
            change.deletions.forEach(item => {
              markdown += `    - \`${formatValue(item)}\`\n`;
            });
          }
          break;
      }
    });

    markdown += '\n';
  });

  // Add summary section
  markdown += `## Summary\n\n`;
  markdown += `Total changes detected: **${changes.summary.totalChanges}**\n\n`;
  markdown += `Generated on: **${new Date(changes.summary.timestamp).toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  })}**\n`;

  // Return in the format expected by n8n
  return [{ json: { markdown } }];
}

// Helper function to format values for markdown display
function formatValue(value) {
  if (value === null || value === undefined) {
    return 'null';
  } else if (typeof value === 'object') {
    if (Array.isArray(value)) {
      // For arrays, simplify to show length
      if (value.length === 0) return '[]';
      if (value.length > 3) {
        return `[Array with ${value.length} items]`;
      }
      // For small arrays, show the items
      return JSON.stringify(value).substring(0, 60) + (JSON.stringify(value).length > 60 ? '...' : '');
    }

    // For objects with a name or key identifier, try to use that
    if (value.name) return value.name;
    if (value.title) return value.title;
    if (value.id) return value.id;
    if (value.planName) return value.planName;
    if (value.question) return value.question;

    // For other objects, shorten to reasonable length
    const objStr = JSON.stringify(value);
    return objStr.substring(0, 60) + (objStr.length > 60 ? '...' : '');
  } else if (typeof value === 'string') {
    // For strings, add quoting
    return value;
  } else {
    // For other primitives, convert to string
    return String(value);
  }
}

// Execute the function and return the markdown result
return generateChangelogMarkdown();
