// In a Function node in n8n
const inputData = $input.all();

function cleanAllPosts(data) {
 return data.map(item => {
 try {
 // Check if item exists and has the expected structure
 if (!item || typeof item !== 'object') {
 return { cleaned_text: '', error: 'Invalid item structure' };
 }

 // Get the text, with multiple fallbacks
 let text = '';
 if (typeof item === 'string') {
 text = item;
 } else if (item.json && item.json.text) {
 text = item.json.text;
 } else if (typeof item.json === 'string') {
 text = item.json;
 } else {
 text = JSON.stringify(item);
 }

 // Make sure text is a string
 text = String(text);

 // Perform the cleaning operations
 try {
 text = text.replace(/&#x2F;/g, '/');
 text = text.replace(/&#x27;/g, "'");
 text = text.replace(/&\w+;/g, ' ');
 text = text.replace(/<[^>]*>/g, '');
 text = text.replace(/\|\s*/g, '| ');
 text = text.replace(/\s+/g, ' ');
 text = text.replace(/\s*(https?:\/\/[^\s]+)\s*/g, '\n$1\n');
 text = text.replace(/\n{3,}/g, '\n\n');
 text = text.trim();
 } catch (cleaningError) {
 console.log('Error during text cleaning:', cleaningError);
 // Return original text if cleaning fails
 return { cleaned_text: text, warning: 'Partial cleaning applied' };
 }

 return { cleaned_text: text };

 } catch (error) {
 console.log('Error processing item:', error);
 return { 
 cleaned_text: '', 
 error: `Processing error: ${error.message}`,
 original: item
 };
 }
 }).filter(result => result.cleaned_text || result.error); 
}

try {
 return cleanAllPosts(inputData);
} catch (error) {
 console.log('Fatal error:', error);
 return [{ 
 cleaned_text: '', 
 error: `Fatal error: ${error.message}`,
 input: inputData 
 }];
}

const items = $input.all();
const updatedItems = items.map((item) => {
  if (item?.json?.data?.port > 5000) {
    item.json["high-port"] = true;
  }
  return item.json;
});
return updatedItems;

return items.map(item => {
  let raw = item.json.cleaned_text;

  try {
    // Replace common problematic characters
    raw = raw
      .replace(/\n/g, '')            // Remove newlines
      .replace(/\r/g, '')            // Remove carriage returns
      .replace(/\t/g, ' ')           // Replace tabs with space
      .replace(/\\u0000/g, '')       // Remove null characters
      .trim();

    // Try parsing the cleaned JSON
    const parsed = JSON.parse(raw);

    // Return only the 'json' field from parsed result
    return {
      json: parsed.json || {}
    };

  } catch (err) {
    // If parsing fails, return the original item with error message
    return {
      json: {
        error: 'Parsing failed',
        reason: err.message,
        original: raw
      }
    };
  }
});

// Filter out the first item (legal notice / metadata)
const jobs = items.filter(item => item.json.id);

// Map and clean each job
return jobs.map(job => ({
  json: {
    id: job.json.id,
    company: job.json.company,
    position: job.json.position,
    location: job.json.location,
    salary_min: job.json.salary_min,
    salary_max: job.json.salary_max,
    tags: job.json.tags,
    logo: job.json.logo,
    description: job.json.description,
    url: job.json.url,
    source: "Remote OK"
  }
}));

return items.map(item => {
  const min = item.json.salary_min;
  const max = item.json.salary_max;

  let salaryString = 'Not specified';

  if (min && max) {
    salaryString = `${min} - ${max}`;
  } else if (min) {
    salaryString = `From ${min}`;
  } else if (max) {
    salaryString = `Up to ${max}`;
  }

  return {
    json: {
      ...item.json,
      salary_string: salaryString
    }
  };
});

function formatJobs(jobs) {
  if (!Array.isArray(jobs)) return [];

  return jobs.map(job => {
    const position = job.position || "No Title";
    const company = job.company || "N/A";
    const location = job.location || "N/A";
    const salary = (job.salary_min && job.salary_max) 
      ? `$${job.salary_min} - $${job.salary_max}` 
      : "N/A";
    const description = job.description 
      ? job.description.substring(0, 1000).replace(/\n/g, ' ') 
      : "N/A";
    const url = job.url || "N/A";
    const source = job.source || "N/A";

    const message = `${position}
ðŸ¢ Company: ${company}
ðŸŒ Location: ${location}
ðŸ’° Salary Range: ${salary}

ðŸ“ Description:
${description}...

ðŸ”— Apply: ${url}
ðŸŒ Company Site: ${source}`;

    return { json: { message } };
  });
}

