const now = new Date();
const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
const tomorrow = new Date(today);
tomorrow.setDate(today.getDate() + 1);

const isoDate = (d) => d.toISOString().split('.')[0] + 'Z';

return [
  {
    json: {
      today: isoDate(today),
      tomorrow: isoDate(tomorrow),
    },
  },
];

const data = $input.all().map(item => item.json);

function generateMeetingReminderEmail(meetings) {
  const escapeHtml = (unsafe) =>
    unsafe
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");

  const meetingCards = meetings.map(meeting => {
    const start = new Date(meeting.meeting_start);
    const end = new Date(meeting.meeting_end);

    const options = { year: 'numeric', month: 'short', day: 'numeric', hour: 'numeric', minute: '2-digit' };
    const timeString = `${start.toLocaleString('en-US', options)} ‚Äì ${end.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`;

    const requiredAttendees = meeting.attendees
      .filter(a => a.type === 'required')
      .map(a => escapeHtml(a.emailAddress.name))
      .join(', ');

    const optionalAttendees = meeting.attendees
      .filter(a => a.type === 'optional')
      .map(a => escapeHtml(a.emailAddress.name))
      .join(', ');

    return `
      <table width="100%" cellspacing="0" cellpadding="0" style="margin-bottom: 20px;">
        <tr>
          <td align="center" style="padding: 0 10px;">
            <table cellpadding="20" cellspacing="0" width="100%" style="max-width: 800px; background-color: #ffffff; border: 1px solid #ddd; border-collapse: collapse;">
              <tr>
                <td style="font-family: Arial, sans-serif; color: #333333; font-size: 14px;">
                  <h2 style="margin: 0 0 10px; font-size: 18px; color: #2c3e50;">${escapeHtml(meeting.subject)}</h2>
                  <p><strong>Time:</strong> ${timeString}</p>
                  <p><strong>Organizer:</strong> ${escapeHtml(meeting.meeting_organizer)} (${escapeHtml(meeting.meeting_organizer_email)})</p>
                  <p><strong>Description:</strong> ${escapeHtml(meeting.description || 'No description provided.')}</p>
                  <p><strong>Attendees:</strong><br>${requiredAttendees || 'None'}<br>
                  ${optionalAttendees ? `<em>Optional:</em> ${optionalAttendees}` : ''}</p>
                  <p>
                    <a href="${meeting.meeting_link}" style="display: inline-block; padding: 10px 15px; background-color: #0078d4; color: #ffffff; text-decoration: none; border-radius: 4px;">View in Calendar</a>
                  </p>
                </td>
              </tr>
            </table>
          </td>
        </tr>
      </table>
    `;
  }).join('\n');

  return `
    <!DOCTYPE html>
    <html>
      <head>
        <meta charset="UTF-8">
        <title>Today's Meetings</title>
      </head>
      <body style="margin: 0; padding: 0; font-family: Arial, sans-serif; background-color: #f4f4f4; color: #333;">
        <table width="100%" cellpadding="0" cellspacing="0" border="0" style="padding: 20px;">
          <tr>
            <td align="center">
              <h1 style="font-size: 24px;">üìÖ Your Meetings Today</h1>
            </td>
          </tr>
          <tr>
            <td>
              ${meetingCards}
            </td>
          </tr>
        </table>
      </body>
    </html>
  `;
}

const htmlContent = generateMeetingReminderEmail(data);

// Return HTML + Subject for email node
return [
  {
    json: {
      subject: "üóìÔ∏è Your Meetings Today ‚Äì " + (new Date()).toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' }),
      html: htmlContent
    }
  }
];
